// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AssetList = () => {
  const navigate = useNavigate();
  const [assets, setAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    // Проверяем текущую сессию
    axios.get('http://localhost:5000/api/check-auth')
      .then(res => {
        setCurrentUser(res.data.user || null);
      });

    // Получаем список активов
    axios.get('http://localhost:5000/api/assets/')
      .then(res => setAssets(res.data))
      .catch(err => console.error('Ошибка загрузки активов:', err));
  }, []);

  const filteredAssets = assets.filter(asset =>
    asset.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.inventory_number?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>Список активов</h2>
      <input
        placeholder="Поиск по названию или инвентарному номеру"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Кнопка доступна только админам */}
      {currentUser && currentUser.role === 'admin' && (
        <button onClick={() => navigate('/assets/new')}>
          Добавить актива
        </button>
      )}

      <table>
        <thead>
          <tr>
            <th>Инвентарный номер</th>
            <th>Наименование</th>
            <th>Местоположение</th>
            <th>Статус</th>
          </tr>
        </thead>
        <tbody>
          {filteredAssets.map((asset) => (
            <tr key={asset.id}>
              <td>{asset.inventory_number}</td>
              <td>{asset.name}</td>
              <td>{asset.location}</td>
              <td>{asset.status_id}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {filteredAssets.length === 0 && <p>Нет записей</p>}
    </div>
  );
};

export default AssetList;
