// src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Snackbar,
  Alert,
  Box
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const [asset, setAsset] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => console.error('Ошибка загрузки данных:', err));
  }, [id]);

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false);
      alert('Данные сохранены');
    } catch (err) {
      alert('Ошибка сохранения');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>{asset?.name}</Typography>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset?.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset?.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Наименование"
              name="name"
              value={asset?.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset?.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset?.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/')}>
            Назад к списку
          </Button>

          {currentUser && currentUser.role === 'admin' && (
            <Button variant="contained" color="success" onClick={handleSave}>
              Сохранить изменения
            </Button>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
