// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Divider,
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react'; // Используем SVG метод

const AssetDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '',
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: 1,
  });

  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // В реальном приложении проверяется через API

  // Загрузка данных об активе и справочников
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetRes = await axios.get(`http://localhost:5000/api/assets/${id}`);
        setAsset(assetRes.data);

        const categoriesRes = await axios.get('http://localhost:5000/api/categories');
        setCategories(categoriesRes.data);

        const statusesRes = await axios.get('http://localhost:5000/api/statuses');
        setStatuses(statusesRes.data);
      } catch (err) {
        console.error('Ошибка загрузки данных:', err);
      }
    };

    fetchData();

    // Пример проверки роли пользователя (заменить на реальную логику)
    const userRole = localStorage.getItem('userRole') || 'user';
    setIsAdmin(userRole === 'admin');
  }, [id]);

  // Обработчик изменения полей формы
  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  // Сохранить изменения
  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false);
      alert('Данные успешно сохранены');
    } catch (err) {
      console.error('Ошибка сохранения:', err);
      alert('Не удалось сохранить данные');
    }
  };

  // Печать QR-кода
  const handlePrintQR = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head><title>Печать QR-кода</title></head>
        <body style="text-align:center; font-family: Arial;">
          <h3>${asset.inventory_number}</h3>
          <svg xmlns="http://www.w3.org/2000/svg" width="200" height="200">
            ${document.getElementById('qrcode').children[0].outerHTML}
          </svg>
          <script>window.print();</script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Paper elevation={3} style={{ padding: '20px', backgroundColor: '#ffffff' }}>
        <Typography variant="h5" gutterBottom align="center">
          Карточка оборудования
        </Typography>
        <Divider />

        <Grid container spacing={2} style={{ marginTop: '10px' }}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="category-label">Категория</InputLabel>
              <Select
                labelId="category-label"
                name="category_id"
                value={asset.category_id || ''}
                onChange={handleChange}
                disabled={!isEditing}
              >
                {categories.map((cat) => (
                  <MenuItem key={cat.id} value={cat.id}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="status-label">Статус</InputLabel>
              <Select
                labelId="status-label"
                name="status_id"
                value={asset.status_id || 1}
                onChange={handleChange}
                disabled={!isEditing}
              >
                {statuses.map((stat) => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              multiline
              rows={4}
              disabled={!isEditing}
              size="small"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center" id="qrcode">
          <QRCodeSVG
            value={asset.inventory_number || 'IT-Актив'}
            size={200}
            bgColor="#ffffff"
            fgColor="#000000"
            level="Q"
            includeMargin
          />
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between" alignItems="center">
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate('/')}
            size="medium"
          >
            Назад к списку
          </Button>

          {isAdmin && (
            <>
              {!isEditing ? (
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => setIsEditing(true)}
                  size="medium"
                >
                  Редактировать
                </Button>
              ) : (
                <>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={() => setIsEditing(false)}
                    size="medium"
                  >
                    Отменить
                  </Button>
                  <Button
                    variant="contained"
                    color="success"
                    onClick={handleSave}
                    size="medium"
                  >
                    Сохранить
                  </Button>
                </>
              )}
            </>
          )}

          <Button
            variant="outlined"
            color="info"
            onClick={handlePrintQR}
            size="medium"
          >
            Печать QR-кода
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
