import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Checkbox,
  Box,
} from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState('inventory_number');
  const [sortOrder, setSortOrder] = useState('asc');
  const [selectedAssets, setSelectedAssets] = useState([]);
  const navigate = useNavigate();
  const navigation = useNavigation();

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const statsRes = await axios.get('http://localhost:5000/api/statuses');
        const empsRes = await axios.get('http://localhost:5000/api/employees');

        setAssets(assetsRes.data);
        setDepartments(depsRes.data);
        setStatuses(statsRes.data);
        setEmployees(empsRes.data);
      } catch (err) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö:', err);
      }
    };

    fetchData();
  }, []);

  // === üõë –ü–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ –ø—Ä–∏ –ø–æ–ø—ã—Ç–∫–µ —É–π—Ç–∏ —Å–æ —Å—Ç—Ä–∞–Ω–∏—Ü—ã ===
  useEffect(() => {
    const handleBeforeUnload = (event) => {
      if (selectedAssets.length > 0 && location.pathname !== '/assets') {
        event.preventDefault(); // –ë–ª–æ–∫–∏—Ä—É–µ–º –ø–µ—Ä–µ—Ö–æ–¥
        const confirmMessage = '–°–Ω—è—Ç—å –≤—ã–¥–µ–ª–µ–Ω–∏–µ –∏ –ø–µ—Ä–µ–π—Ç–∏ –Ω–∞ –¥—Ä—É–≥—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É?';
        event.returnValue = confirmMessage; // –î–ª—è —Å—Ç–∞—Ä—ã—Ö –±—Ä–∞—É–∑–µ—Ä–æ–≤
        return confirmMessage;
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [selectedAssets]);

  // === üîí –ë–ª–æ–∫–∏—Ä–æ–≤–∫–∞ –ø–µ—Ä–µ—Ö–æ–¥–∞ —á–µ—Ä–µ–∑ –Ω–∞–≤–∏–≥–∞—Ü–∏—é ===
  useEffect(() => {
    const unblock = navigate((tx) => {
      if (selectedAssets.length > 0) {
        const userConfirmed = window.confirm(
          '–°–Ω—è—Ç—å –≤—ã–¥–µ–ª–µ–Ω–∏–µ –∏ –ø–µ—Ä–µ–π—Ç–∏ –Ω–∞ –¥—Ä—É–≥—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É?'
        );

        if (userConfirmed) {
          setSelectedAssets([]); // –°–Ω–∏–º–∞–µ–º –≤—ã–¥–µ–ª–µ–Ω–∏–µ
          tx.retry(); // –†–∞–∑—Ä–µ—à–∞–µ–º –ø–µ—Ä–µ—Ö–æ–¥
        } else {
          tx.cancel(); // –ë–ª–æ–∫–∏—Ä—É–µ–º –ø–µ—Ä–µ—Ö–æ–¥
        }
      } else {
        tx.retry(); // –ü–µ—Ä–µ—Ö–æ–¥ –±–µ–∑ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
      }
    });

    return () => {
      unblock();
    };
  }, [navigate, selectedAssets]);

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∞—Ç—ã –≤ DD.MM.YYYY
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    if (isNaN(date)) return '';

    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();

    return `${day}.${month}.${year}`;
  };

  // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –§–ò–û ‚Üí –ò–≤–∞–Ω–æ–≤ –ò.–ò.
  const formatEmployeeName = (employeeId) => {
    if (!employeeId) return '–ù–µ —É–∫–∞–∑–∞–Ω';

    const employee = employees.find(emp => emp.id === employeeId);
    if (!employee) return '–ù–µ –Ω–∞–π–¥–µ–Ω';

    const parts = employee.name.split(' ');
    const surname = parts[0] || '';
    const firstInitial = parts[1]?.charAt(0).toUpperCase() || '';
    const patronymicInitial = parts[2]?.charAt(0).toUpperCase() || '';

    return `${surname} ${firstInitial + '.'}${patronymicInitial + '.'}`;
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∏–º–µ–Ω–∏ —Å—Ç–∞—Ç—É—Å–∞
  const getStatusName = (statusId) => {
    const status = statuses.find(stat => stat.id === statusId);
    return status?.name || '–ù–µ —É–∫–∞–∑–∞–Ω';
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∏–º–µ–Ω–∏ –æ—Ç–¥–µ–ª–∞
  const getDepartmentName = (departmentId) => {
    const department = departments.find(d => d.id === departmentId);
    return department?.name || '–ù–µ —É–∫–∞–∑–∞–Ω';
  };

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –ø–æ —Å—Ç—Ä–æ–∫–µ –ø–æ–∏—Å–∫–∞
  const filteredAssets = assets.filter(asset => {
    const searchLower = searchTerm.toLowerCase();

    const assetFieldsToSearch = [
      asset.inventory_number,
      `${asset.type} ${asset.brand} ${asset.model}`,
      getStatusName(asset.status_id),
      formatEmployeeName(asset.responsible_person),
      getDepartmentName(asset.department_id),
      asset.room || '',
      formatDate(asset.purchase_date),
    ].join(' ').toLowerCase();

    return assetFieldsToSearch.includes(searchLower);
  });

  // –°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –∞–∫—Ç–∏–≤–æ–≤
  const sortedAssets = [...filteredAssets].sort((a, b) => {
    let comparison = 0;

    switch (sortColumn) {
      case 'inventory_number':
        comparison = a.inventory_number.localeCompare(b.inventory_number, undefined, { numeric: true });
        break;
      case 'name':
        comparison = a.type.localeCompare(b.type, undefined, { sensitivity: 'base' });
        break;
      case 'brand':
        comparison = a.brand.localeCompare(b.brand, undefined, { sensitivity: 'base' });
        break;
      case 'model':
        comparison = a.model.localeCompare(b.model, undefined, { sensitivity: 'base' });
        break;
      case 'status':
        const statusA = getStatusName(a.status_id).toLowerCase();
        const statusB = getStatusName(b.status_id).toLowerCase();
        comparison = statusA.localeCompare(statusB);
        break;
      case 'responsible':
        const empA = formatEmployeeName(a.responsible_person).toLowerCase();
        const empB = formatEmployeeName(b.responsible_person).toLowerCase();
        comparison = empA.localeCompare(empB);
        break;
      case 'department':
        const deptA = getDepartmentName(a.department_id).toLowerCase();
        const deptB = getDepartmentName(b.department_id).toLowerCase();
        comparison = deptA.localeCompare(deptB);
        break;
      case 'room':
        const roomA = (a.room || '').toLowerCase();
        const roomB = (b.room || '').toLowerCase();
        comparison = roomA.localeCompare(roomB);
        break;
      case 'purchase_date':
        comparison = new Date(a.purchase_date) - new Date(b.purchase_date);
        break;
      default:
        return 0;
    }

    if (sortOrder === 'desc') {
      comparison = -comparison;
    }

    return comparison;
  });

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –Ω–∞ –∑–∞–≥–æ–ª–æ–≤–æ–∫ —Å—Ç–æ–ª–±—Ü–∞
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —á–µ–∫–±–æ–∫—Å–∞
  const handleCheckboxChange = (id) => {
    setSelectedAssets(prev =>
      prev.includes(id)
        ? prev.filter(assetId => assetId !== id)
        : [...prev, id]
    );
  };

  // –£–¥–∞–ª–µ–Ω–∏–µ –≤—ã–±—Ä–∞–Ω–Ω—ã—Ö –∞–∫—Ç–∏–≤–æ–≤
  const handleDeleteSelected = async () => {
    if (!window.confirm(`–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å ${selectedAssets.length} –∞–∫—Ç–∏–≤–æ–≤?`)) return;

    try {
      await axios.delete('http://localhost:5000/api/assets', {
        data: { ids: selectedAssets },
      });

      // –£–±–∏—Ä–∞–µ–º —É–¥–∞–ª–µ–Ω–Ω—ã–µ –∞–∫—Ç–∏–≤—ã –∏–∑ —Å–ø–∏—Å–∫–∞
      setAssets(prev => prev.filter(asset => !selectedAssets.includes(asset.id)));
      setSelectedAssets([]);
      alert('–í—ã–±—Ä–∞–Ω–Ω—ã–µ –∞–∫—Ç–∏–≤—ã —É—Å–ø–µ—à–Ω–æ —É–¥–∞–ª–µ–Ω—ã');
    } catch (err) {
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –∞–∫—Ç–∏–≤–æ–≤');
      console.error(err);
    }
  };

  // –ü–µ—á–∞—Ç—å QR-–∫–æ–¥–æ–≤
  const handlePrintQRCode = async (asset) => {
    if (selectedAssets.length === 0) {
      alert('–í—ã–±–µ—Ä–∏—Ç–µ —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω –∞–∫—Ç–∏–≤');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/qrcodes', {
        ids: selectedAssets
      });
      const inventoryNumber = asset.inventory_number;
      const name = asset.name || `${asset.type} ${asset.brand} ${asset.model}`;
      const timestamp = new Date().getTime();
      const fileName = `${timestamp}.pdf`;
      const codes = res.data;

      let htmlContent = `
        <html>
          <head>
            <style>
              @page {
                size: A4;
                margin: 0;
                @top-center { content: ''; }
                @bottom-left { content: ''; }
                @bottom-right { content: ''; }
              }
              body {
                display: flex;
                flex-wrap: wrap;
                gap: 10px;
                padding: 20px;
                background-color: #fff;
              }
              .qr-wrapper {
                width: 3cm; /* –®–∏—Ä–∏–Ω–∞ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞ */
                height: 3cm; /* –í—ã—Å–æ—Ç–∞ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞ */
                position: relative;
                font-size: 10px;
                text-align: center;
              }
              .qr-container {
                width: 100%;
                height: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
              }
              img {
                max-width: 100%;
                max-height: 100%;
                object-fit: contain;
              }
              .text-top, .text-bottom {
                position: absolute;
                left: 50%; /* –¶–µ–Ω—Ç—Ä–∏—Ä–æ–≤–∞–Ω–∏–µ –ø–æ –≥–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª–∏ */
                transform: translateX(-50%); /* –ö–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∫–∞ —Ü–µ–Ω—Ç—Ä–∏—Ä–æ–≤–∞–Ω–∏—è */
                white-space: nowrap; /* –ó–∞–ø—Ä–µ—Ç –ø–µ—Ä–µ–Ω–æ—Å–∞ —Å—Ç—Ä–æ–∫–∏ */
                overflow: hidden; /* –°–∫—Ä—ã–≤–∞–µ—Ç –ª–∏—à–Ω–∏–π —Ç–µ–∫—Å—Ç */
                text-overflow: ellipsis; /* –î–æ–±–∞–≤–ª—è–µ—Ç –º–Ω–æ–≥–æ—Ç–æ—á–∏–µ –ø—Ä–∏ –æ–±—Ä–µ–∑–∫–µ */
                width: 90%; /* –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º —à–∏—Ä–∏–Ω—É —Ç–µ–∫—Å—Ç–∞ */
                font-size: 8px; /* –ú–∞–ª–µ–Ω—å–∫–∏–π —Ä–∞–∑–º–µ—Ä —à—Ä–∏—Ñ—Ç–∞ */
              }
              .text-top {
                top: 0;
              }
              .text-bottom {
                bottom: 0;
              }
            </style>
          </head>
          <body>
      `;

      codes.forEach((code) => {
        htmlContent += `
          <div class="qr-wrapper">
            <div class="qr-container">
              <img src="data:image/png;base64,${code.qr_base64}" />
            </div>
            <span class="text-top">${code.name}</span>
            <span class="text-bottom">${code.inventory_number}</span>
          </div>
        `;
      });

htmlContent += '</body></html>';

      const printWindow = window.open('', '_blank');
      printWindow.document.write(htmlContent);

      setTimeout(() => {
      // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –∏–º–µ–Ω–∏ —Ñ–∞–π–ª–∞ –ø—Ä–∏ —Å–∫–∞—á–∏–≤–∞–Ω–∏–∏
      printWindow.document.title = fileName;
      printWindow.document.head.innerHTML += `
      <meta http-equiv="Content-Disposition" content="attachment; filename=${fileName}" />
      `;

        printWindow.print();
        printWindow.close();
      }, 500);
    } catch (err) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ QR-–∫–æ–¥–æ–≤:', err);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å QR-–∫–æ–¥—ã —Å —Å–µ—Ä–≤–µ—Ä–∞');
    }
  };

  return (
    <Container maxWidth="">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>–°–ø–∏—Å–æ–∫ –æ–±–æ—Ä—É–¥–æ–≤–∞–Ω–∏—è</strong>
        </Typography>

        {/* –ü–æ–∏—Å–∫–æ–≤–∞—è —Å—Ç—Ä–æ–∫–∞ */}
        <TextField
          label="–ü–æ–∏—Å–∫"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* –ö–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        <Box display="flex" alignItems="center" mb={2}>
          <Button
            variant="contained"
            color="primary"
            disabled={selectedAssets.length === 0}
            onClick={handlePrintQRCode}
            style={{ marginRight: '8px' }}
          >
            –ü–µ—á–∞—Ç—å QR
          </Button>
          <Button
            variant="contained"
            color="error"
            disabled={selectedAssets.length === 0}
            onClick={handleDeleteSelected}
            style={{ marginRight: '8px' }}
          >
            –£–¥–∞–ª–∏—Ç—å
          </Button>
          <Button
            variant="contained"
            color="success"
            onClick={() => navigate('/assets/new')}
          >
            –î–æ–±–∞–≤–∏—Ç—å
          </Button>
        </Box>

        {/* –¢–∞–±–ª–∏—Ü–∞ –∞–∫—Ç–∏–≤–æ–≤ */}
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell></TableCell>
              <TableCell
                onClick={() => handleSort('inventory_number')}
                sx={{ cursor: 'pointer' }}
              >
                –ò–Ω–≤–µ–Ω—Ç–∞—Ä–Ω—ã–π –Ω–æ–º–µ—Ä{' '}
                {sortColumn === 'inventory_number' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('name')}
                sx={{ cursor: 'pointer' }}
              >
                –ù–∞–∏–º–µ–Ω–æ–≤–∞–Ω–∏–µ{' '}
                {sortColumn === 'name' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('status')}
                sx={{ cursor: 'pointer' }}
              >
                –°—Ç–∞—Ç—É—Å{' '}
                {sortColumn === 'status' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('responsible')}
                sx={{ cursor: 'pointer' }}
              >
                –û—Ç–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω—ã–π{' '}
                {sortColumn === 'responsible' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('department')}
                sx={{ cursor: 'pointer' }}
              >
                –û—Ç–¥–µ–ª{' '}
                {sortColumn === 'department' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('room')}
                sx={{ cursor: 'pointer' }}
              >
                –ü–æ–º–µ—â–µ–Ω–∏–µ{' '}
                {sortColumn === 'room' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
              <TableCell
                onClick={() => handleSort('purchase_date')}
                sx={{ cursor: 'pointer' }}
              >
                –î–∞—Ç–∞ –ø–æ–∫—É–ø–∫–∏{' '}
                {sortColumn === 'purchase_date' && <span>{sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>}
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {sortedAssets.map((asset) => (
              <TableRow
                key={asset.id}
                hover
                onClick={() => navigate(`/assets/${asset.id}`)}
                style={{ cursor: 'pointer' }}
              >
                <TableCell>
                  <Checkbox
                    checked={selectedAssets.includes(asset.id)}
                    onClick={(e) => e.stopPropagation()}
                    onChange={(e) => handleCheckboxChange(asset.id)}
                  />
                </TableCell>
                <TableCell>{asset.inventory_number}</TableCell>
                <TableCell>{`${asset.type || ''} ${asset.brand || ''} ${asset.model || ''}`.trim() || '-'}</TableCell>
                <TableCell>{getStatusName(asset.status_id)}</TableCell>
                <TableCell>{formatEmployeeName(asset.responsible_person)}</TableCell>
                <TableCell>{getDepartmentName(asset.department_id)}</TableCell>
                <TableCell>{asset.room || '-'}</TableCell>
                <TableCell>{asset.purchase_date ? formatDate(asset.purchase_date) : '-'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
