// frontend/src/App.js

import React, { useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';

import Sidebar from './components/Sidebar';
import AssetList from './components/AssetList';
import AddAsset from './components/AddAsset';
import AssetDetails from './components/AssetDetails';
import EmployeeList from './components/EmployeeList';
import Directory from './components/Directory';
import ChangesList from './components/ChangesList';
import Login from './components/Login';
import Header from './components/Header';

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh', flexDirection: 'column' }}>
        {/* Шапка сайта */}
        <Header currentUser={currentUser} onLogin={(user) => setCurrentUser(user)} />

        <div style={{ display: 'flex', flex: 1 }}>
          {/* Боковое меню */}
          {currentUser && <Sidebar />}

          {/* Основной контент */}
          <main style={{
            marginLeft: currentUser ? 250 : 0,
            flex: 1,
            backgroundColor: '#fff',
            padding: '20px'
          }}>
            <Routes>
              {/* Публичный доступ */}
              <Route path="/assets" element={<AssetList currentUser={currentUser} />} />
              <Route path="/assets/:id" element={<AssetDetails currentUser={currentUser} />} />

              {/* Только для админа */}
              <Route path="/assets/new" element={
                currentUser ? <AddAsset currentUser={currentUser} /> : <Navigate to="/login" />
              } />
              <Route path="/employees" element={
                currentUser ? <EmployeeList currentUser={currentUser} /> : <Navigate to="/login" />
              } />
              <Route path="/directory" element={
                currentUser ? <Directory currentUser={currentUser} /> : <Navigate to="/login" />
              } />
              <Route path="/changes" element={
                currentUser ? <ChangesList currentUser={currentUser} /> : <Navigate to="/login" />
              } />

              {/* Страница входа */}
              <Route path="/login" element={
                currentUser ? <Navigate to="/assets" /> : <Login />
              } />

              {/* Редирект по умолчанию */}
              <Route path="/" element={<AssetList currentUser={currentUser} />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
