import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/departments');
        setDepartments(res.data);
      } catch (err) {
        console.error('Ошибка при загрузке отделов:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Отделы
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Название</TableCell>
              <TableCell align="right">Сотрудников</TableCell>
              <TableCell align="right">Оборудования</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {departments.map(dept => (
              <TableRow key={dept.id}>
                <TableCell>{dept.name}</TableCell>
                <TableCell align="right">0</TableCell> {/* Здесь должен быть подсчёт сотрудников */}
                <TableCell align="right">0</TableCell> {/* Здесь должен быть подсчёт оборудования */}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default DepartmentList;
