// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../AuthContext';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableRow,
  Button
} from '@mui/material';

const AssetList = () => {
  const { currentUser } = useAuth(); // Получаем текущего пользователя из контекста
  const [assets, setAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/assets/')
      .then(res => setAssets(res.data))
      .catch(err => console.error('Ошибка загрузки активов:', err));
  }, []);

  const filteredAssets = assets.filter(asset =>
    asset.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.inventory_number.includes(searchTerm)
  );

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Список активов</Typography>
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '20px' }}
        />

        <TableContainer component={Paper}>
          <Table>
            <TableBody>
              {filteredAssets.length > 0 ? (
                filteredAssets.map((asset) => (
                  <TableRow key={asset.id}>
                    <TableCell>{asset.inventory_number}</TableCell>
                    <TableCell>{asset.name}</TableCell>
                    <TableCell>{asset.location}</TableCell>
                    <TableCell>{asset.status_id}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={4} align="center">Нет записей</TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        {/* Кнопки управления */}
        {currentUser && currentUser.role === 'admin' && (
          <Button variant="contained" color="primary">
            Добавить актива
          </Button>
        )}
      </Paper>
    </Container>
  );
};

export default AssetList;
