// frontend/src/App.js

import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login'; // Можно удалить, если не используется
import AssetList from './components/AssetList';
import Sidebar from './components/Sidebar';
import AddAsset from './components/AddAsset';
import AssetDetails from './components/AssetDetails';
import Directory from './components/Directory';
import DepartmentList from './components/DepartmentList';

function App() {
  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Боковое меню */}
        <div style={{ width: 250, backgroundColor: '#f4f4f4', height: '100vh', position: 'fixed', top: 0, left: 0 }}>
          <Sidebar />
        </div>

        {/* Основной контент */}
        <div style={{ marginLeft: 250, flex: 1, backgroundColor: '#f9f9f9', padding: '20px' }}>
          <Routes>
            {/* Страница активов */}
            <Route path="/assets" element={<AssetList />} />

            {/* Детальная карточка актива */}
            <Route path="/assets/:id" element={<AssetDetails />} />

            {/* Добавление актива */}
            <Route path="/assets/new" element={<AddAsset />} />

            {/* Страница входа */}
            <Route path="/login" element={<Login />} />

            {/* Справочник */}
            <Route path="/directory" element={<Directory />} />

            {/* Отделы */}
            <Route path="/departments" element={<DepartmentList />} />

            {/* Сотрудники */}
            <Route path="/employees" element={<EmployeeList />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
