import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';

const formatDate = (dateString) => {
  if (!dateString) return '-';
  const date = new Date(dateString);
  const day = String(date.getDate()).padStart(2, '0');
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const year = date.getFullYear();
  const hours = String(date.getHours()).padStart(2, '0');
  const minutes = String(date.getMinutes()).padStart(2, '0');
  const seconds = String(date.getSeconds()).padStart(2, '0');

  return `${day}.${month}.${year} ${hours}:${minutes}:${seconds}`;
};

const MovesList = () => {
  const [moves, setMoves] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/moves')
      .then(res => setMoves(res.data))
      .catch(err => console.error('Ошибка загрузки перемещений:', err));
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          История перемещений
        </Typography>

        <TableContainer>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell>Дата</TableCell>
                <TableCell>Инвентарный номер</TableCell>
                <TableCell>Наименование</TableCell>
                <TableCell>Из помещения</TableCell>
                <TableCell>В помещение</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {moves.length > 0 ? (
                moves.map((move) => (
                  <TableRow key={move.id}>
                    <TableCell>{formatDate(move.changed_at)}</TableCell>
                    <TableCell>{move.inventory_number || '-'}</TableCell>
                    <TableCell>{move.asset_name || '-'}</TableCell>
                    <TableCell>{move.from_room || '-'}</TableCell>
                    <TableCell>{move.to_room || '-'}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={5} align="center">
                    Нет записей о перемещении
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default MovesList;
