// frontend/src/components/AddAsset.js

import React, { useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button
} from '@mui/material';

const AddAsset = () => {
  const [form, setForm] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '',
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/assets/', form);
      alert('Актив создан');
    } catch (err) {
      alert('Ошибка создания');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Добавить актива</Typography>
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={form.serial_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={form.inventory_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Местоположение"
              name="location"
              value={form.location}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={form.status_id}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Создать
        </Button>
      </Paper>
    </Container>
  );
};

export default AddAsset;
