import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AddAsset = ({currentUser}) => {
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type: '', // Новое поле
    brand: '', // Обязательное
    model: '', // Обязательное
    purchase_date: '',
    category_id: '', // Обязательное
    status_id: '', // Обязательное
    actual_user: '',
    responsible_person: '', // ID сотрудника
    room: '',
    department_id: '', // Автоматически заполняется
    comments: '',
  });

  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [errors, setErrors] = useState({});

  // Загружаем справочники
  useEffect(() => {
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data))
      .catch(err => console.error('Ошибка загрузки категорий:', err));

    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data))
      .catch(err => console.error('Ошибка загрузки статусов:', err));

    axios.get('http://localhost:5000/api/departments')
      .then(res => setDepartments(res.data))
      .catch(err => console.error('Ошибка загрузки отделов:', err));

    axios.get('http://localhost:5000/api/employees')
      .then(res => setEmployees(res.data))
      .catch(err => console.error('Ошибка загрузки сотрудников:', err));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Если это изменение ответственного сотрудника
    if (name === 'responsible_person') {
      const employee = employees.find(emp => emp.id === Number(value));
      if (employee) {
        // Автоматически проставляем отдел
        setAsset(prev => ({
          ...prev,
          [name]: value,
          department_id: employee.department_id || ''
        }));
      } else {
        setAsset(prev => ({ ...prev, [name]: value }));
      }
    } else {
      setAsset(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async () => {
    // Проверка обязательных полей
    const newErrors = {};
    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.category_id) newErrors.category_id = 'Обязательное поле';
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!asset.responsible_person) newErrors.responsible_person = 'Обязательное поле';
    if (!asset.purchase_date) newErrors.purchase_date = 'Обязательное поле';


    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/assets', asset);
      alert('Оборудование успешно добавлено');
      navigate(`/assets/${res.data.id}`);
    } catch (err) {
      alert('Ошибка при добавлении оборудования');
      console.error(err);
    }
  };

  if (!currentUser || currentUser.role !== 'admin') {
    return <Navigate to="/assets" replace />;
  }
  
  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Добавить новое оборудование
        </Typography>

      <Grid container spacing={2}>
        {/* Пара 1: Серийный номер | Инвентарный номер */}
        <Grid item xs={6}>
          <TextField
            label="Серийный номер"
            name="serial_number"
            value={asset.serial_number || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Инвентарный номер *"
            name="inventory_number"
            value={asset.inventory_number || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            error={!!errors.inventory_number}
            helperText={errors.inventory_number}
          />
        </Grid>

        {/* Пара 2: Категория | Фирма */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.category_id}>
            <InputLabel id="category-label">Категория *</InputLabel>
            <Select
              labelId="category-label"
              label="Категория *"
              name="category_id"
              value={asset.category_id || ''}
              onChange={handleChange}
            >
              {categories.map(cat => (
                <MenuItem key={cat.id} value={cat.id}>
                  {cat.name}
                </MenuItem>
              ))}
            </Select>
            {errors.category_id && (
              <Typography color="error" variant="caption">
                {errors.category_id}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Фирма"
            name="brand"
            value={asset.brand || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>

        {/* Пара 3: Модель | Конфигурация */}
        <Grid item xs={6}>
          <TextField
            label="Модель"
            name="model"
            value={asset.model || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Конфигурация (Процессор/DDR/HDD)"
            name="type"
            value={asset.type || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>

        {/* Пара 4: Статус | Фактический пользователь */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.status_id}>
            <InputLabel id="status-label">Статус *</InputLabel>
            <Select
              labelId="status-label"
              label="Статус *"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
            >
              {statuses.map(stat => (
                <MenuItem key={stat.id} value={stat.id}>
                  {stat.name}
                </MenuItem>
              ))}
            </Select>
            {errors.status_id && (
              <Typography color="error" variant="caption">
                {errors.status_id}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Фактический пользователь"
            name="actual_user"
            value={asset.actual_user || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>

        {/* Пара 5: Ответственный | Отдел (подразделение) */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.responsible_person}>
            <InputLabel id="responsible-person-label">Ответственный *</InputLabel>
            <Select
              labelId="responsible-person-label"
              label="Ответственный *"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
            >
              {employees.map(emp => (
                <MenuItem key={emp.id} value={emp.id}>
                  {emp.name}
                </MenuItem>
              ))}
            </Select>
            {errors.responsible_person && (
              <Typography color="error" variant="caption">
                {errors.responsible_person}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal">
            <InputLabel id="department-label">Отдел (подразделение)</InputLabel>
            <Select
              labelId="department-label"
              label="Отдел (подразделение)"
              name="department_id"
              value={asset.department_id || ''}
              onChange={handleChange}
              disabled
              >
              {departments.map(dept => (
                <MenuItem key={dept.id} value={dept.id}>
                  {dept.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>

        {/* Пара 6: Помещение | Дата покупки */}
        <Grid item xs={6}>
          <TextField
            label="Помещение"
            name="room"
            value={asset.room || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Дата покупки *"
            name="purchase_date"
            type="date"
            value={asset.purchase_date || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            error={!!errors.purchase_date}
            helperText={errors.purchase_date}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </Grid>

        {/* Комментарий на всю ширину */}
        <Grid item xs={12}>
          <TextField
            label="Комментарий"
            name="comments"
            value={asset.comments || ''}
            onChange={handleChange}
            fullWidth
            multiline
            rows={4}
            margin="normal"
          />
        </Grid>
      </Grid>

        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/assets')}>
            Отмена
          </Button>
          <Button variant="contained" color="success" onClick={handleSubmit}>
            Добавить
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default AddAsset;
