// frontend/src/components/EmployeeList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]); 
  const [assetCounts, setAssetCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем сотрудников
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const emps = empsRes.data;
        setEmployees(emps);

        // Для каждого сотрудника загружаем количество активов
        const assetCounts = {};

        for (let emp of emps) {
          const deptAssets = await axios.get(`http://localhost:5000/api/assets?responsible_person=${emp.surname}`);
          assetCounts[emp.id] = deptAssets.data.length;
        }

        setAssetCounts(assetCounts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Фамилия</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Отчество</TableCell>
              <TableCell>Должность</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Помещение</TableCell>
              <TableCell>Вн. телефон</TableCell>
              <TableCell align="right">Оборудования</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {employees.map(emp => {
              const dept = departments.find(d => d.id === emp.department_id);
              return (
                <TableRow key={emp.id}>
                  <TableCell>{emp.surname}</TableCell>
                  <TableCell>{emp.name}</TableCell>
                  <TableCell>{emp.patronymic || '-'}</TableCell>
                  <TableCell>{emp.position || '-'}</TableCell>
                  <TableCell>{dept?.name || 'Не указано'}</TableCell>
                  <TableCell>{emp.room || '-'}</TableCell>
                  <TableCell>{emp.internal_phone || '-'}</TableCell>
                  <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default EmployeeList;
