// frontend/src/components/AssetDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Divider,
  Box,
} from '@mui/material';
import QRCode from 'qrcode.react';

const AssetDetails = () => {
  const { id } = useParams();
  const [asset, setAsset] = useState(null);

  useEffect(() => {
    // Запрос к API для получения информации об активе
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => {
        setAsset(res.data);
      })
      .catch(err => {
        console.error('Ошибка загрузки данных:', err);
      });
  }, [id]);

  if (!asset) {
    return <Typography>Загрузка...</Typography>;
  }

  return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>
          {asset.name}
        </Typography>
        <Divider />

        <Grid container spacing={2} style={{ marginTop: '10px' }}>
          <Grid item xs={6}>
            <Typography><strong>Серийный номер:</strong> {asset.serial_number}</Typography>
            <Typography><strong>Инвентарный номер:</strong> {asset.inventory_number}</Typography>
            <Typography><strong>Местоположение:</strong> {asset.location}</Typography>
            <Typography><strong>Статус:</strong> {asset.status}</Typography>
          </Grid>
          <Grid item xs={6}>
            <Typography><strong>Ответственный:</strong> {asset.responsible_person}</Typography>
            <Typography><strong>Фактический пользователь:</strong> {asset.actual_user}</Typography>
            <Typography><strong>Комментарии:</strong> {asset.comments || '-'}</Typography>
          </Grid>
        </Grid>

        <Box mt={3} display="flex" justifyContent="center">
          <QRCode value={asset.inventory_number} size="200x200" />
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
