// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams(); // Получаем ID из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '',
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: ''
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    if (id && id !== 'new') {
      // Загружаем актив по ID
      axios.get(`http://localhost:5000/api/assets/${id}`)
        .then(res => setAsset(res.data))
        .catch(err => console.error('Ошибка загрузки актива:', err));
    }
  }, [id]);

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      if (id === 'new') {
        // Создание нового актива
        await axios.post('http://localhost:5000/api/assets', asset);
      } else {
        // Редактирование существующего актива
        await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      }
      alert(id === 'new' ? 'Актив успешно добавлен' : 'Изменения сохранены');
      navigate('/');
    } catch (err) {
      alert('Ошибка при сохранении данных');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>
          {id === 'new' ? 'Добавить новый актив' : `Редактировать актив: ${asset.name || ''}`}
        </Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
              disabled={!isEditing && id !== 'new'}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>

          {currentUser?.role === 'admin' && (
            <>
              {!isEditing && id !== 'new' && (
                <Button variant="contained" color="primary" onClick={handleEdit}>
                  Редактировать
                </Button>
              )}

              {(isEditing || id === 'new') && (
                <Button variant="contained" color="success" onClick={handleSave}>
                  Сохранить изменения
                </Button>
              )}
            </>
          )}
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
