import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Table,
  TableHead,
  TableRow,
  TableCell,
  Button
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [categories, setCategories] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем активы
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const assetsData = assetsRes.data;
        setAssets(assetsData);

        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const depsData = depsRes.data;
        setDepartments(depsData);

        // Загружаем категории и статусы
        const catsRes = await axios.get('http://localhost:5000/api/categories');
        const statsRes = await axios.get('http://localhost:5000/api/statuses');

        setCategories(catsRes.data);
        setStatuses(statsRes.data);

        // Загружаем сотрудников
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const empsData = empsRes.data;
        console.log('Загруженные сотрудники:', empsData); // Логируем данные сотрудников
        setEmployees(empsData);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  const getStatusName = (statusId) => {
    const status = statuses.find(stat => stat.id === statusId);
    return status?.name || 'Не указано';
  };

  const getDepartmentName = (departmentId) => {
    const dept = departments.find(d => d.id === departmentId);
    return dept?.name || 'Не указано';
  };

  const formatEmployeeName = (employeeId) => {
    if (!employeeId) return '-';
    const employee = employees.find(emp => emp.id === employeeId);
    if (!employee) return '-';

    const { surname, name, patronymic } = employee;
    const firstNameInitial = name.charAt(0).toUpperCase();
    const patronymicInitial = patronymic.charAt(0).toUpperCase();

    return `${surname} ${firstNameInitial}.${patronymicInitial}.`;
  };

  const filteredAssets = assets.filter(asset =>
    Object.values(asset).some(value =>
      String(value)
        .toLowerCase()
        .includes(searchTerm.toLowerCase())
    )
  );

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>Список оборудования</strong>
        </Typography>

        {/* Поисковая строка */}
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* Кнопка добавления актива */}
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/assets/new')}
          style={{ marginBottom: '16px' }}
        >
          Новое оборудование
        </Button>

        {/* Таблица активов */}
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell style={{ fontSize: '12px' }}>Инвентарный номер</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Наименование</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Статус</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Ответственный</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Отдел</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Дата покупки</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredAssets.map((asset) => (
              <TableRow
                key={asset.id}
                hover
                onClick={() => navigate(`/assets/${asset.id}`)}
                style={{ cursor: 'pointer' }}
              >
                <TableCell>{asset.inventory_number}</TableCell>
                <TableCell>{`${asset.type || ''} ${asset.brand || ''} ${asset.model || ''}`.trim() || '-'}</TableCell>
                <TableCell>{getStatusName(asset.status_id)}</TableCell>
                <TableCell>
                  {formatEmployeeName(asset.responsible_person)} {/* Используем formatEmployeeName */}
                </TableCell>
                <TableCell>{getDepartmentName(asset.department_id)}</TableCell>
                <TableCell>
                  {asset.purchase_date ? new Date(asset.purchase_date).toLocaleDateString() : '-'}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
