// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Button
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // Загружаем данные при монтировании
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const statusesRes = await axios.get('http://localhost:5000/api/statuses');

        setAssets(assetsRes.data);
        setStatuses(statusesRes.data);
      } catch (err) {
        console.error('Ошибка загрузки данных:', err);
      }
    };

    fetchData();
  }, []);

  // Функция для получения имени статуса по ID
  const getStatusName = (statusId) => {
    const status = statuses.find(stat => stat.id === statusId);
    return status ? status.name : 'Неизвестный статус';
  };

  // Фильтрация активов
  const filteredAssets = assets.filter(asset =>
    `${asset.type || ''} ${asset.brand || ''} ${asset.model || ''}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.inventory_number?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Список активов</Typography>
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '20px' }}
        />
        {/* Кнопка "Добавить актива" */}
        <Button
          variant="contained"
          color="primary"
          style={{ marginBottom: '20px' }}
          onClick={() => navigate('/assets/new')}
        >
          Добавить актив
        </Button>
        <Table>
          <TableBody>
            {filteredAssets.map((asset) => (
              <TableRow
                key={asset.id}
                hover
                onClick={() => navigate(`/assets/${asset.id}`)}
                style={{ cursor: 'pointer' }}
              >
                <TableCell>{asset.serial_number}</TableCell>
                <TableCell>{asset.inventory_number}</TableCell>
                <TableCell>{`${asset.type} ${asset.brand} ${asset.model}`}</TableCell>
                <TableCell>{asset.location}</TableCell>
                <TableCell>{getStatusName(asset.status_id)}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
