// frontend/src/components/Sidebar.js

import React from 'react';
import { Box, List, ListItem, ListItemButton, ListItemIcon, ListItemText } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import DevicesIcon from '@mui/icons-material/Devices';
import PeopleIcon from '@mui/icons-material/People';
import BookIcon from '@mui/icons-material/Book';
import HistoryIcon from '@mui/icons-material/History';
import { Link } from 'react-router-dom';

const navigate = useNavigate();
const navigateWithConfirm = (path) => {
  if (selectedAssets.length > 0) {
    const userConfirmed = window.confirm(
      'Снять выделение и перейти на другую страницу?'
    );
    if (!userConfirmed) return; // Переход заблокирован
    setSelectedAssets([]); // Снимаем выделение
  }
  navigate(path);
};

const Sidebar = ({}) => {


  return (
    <Box sx={{ width: 250, backgroundColor: '#f4f4f4', height: '100vh', position: 'sticky', top: 0 }}>
      <List>
        <ListItem disablePadding>
          <ListItemButton onClick={() => navigateWithConfirm('/assets')}>
            <ListItemIcon><DevicesIcon /></ListItemIcon>
            <ListItemText primary="Оборудование" />
          </ListItemButton>
        </ListItem>

        <ListItem disablePadding>
          <ListItemButton onClick={() => navigateWithConfirm('/employees')}>
            <ListItemIcon><PeopleIcon /></ListItemIcon>
            <ListItemText primary="Сотрудники" />
          </ListItemButton>
        </ListItem>

        <ListItem disablePadding>
          <ListItemButton onClick={() => navigateWithConfirm('/directory')}>
            <ListItemIcon><BookIcon /></ListItemIcon>
            <ListItemText primary="Справочник" />
          </ListItemButton>
        </ListItem>        

        <ListItem disablePadding>
          <ListItemButton onClick={() => navigateWithConfirm('/changes')}>
            <ListItemIcon><HistoryIcon /></ListItemIcon>
            <ListItemText primary="Изменения" />
          </ListItemButton>
        </ListItem> 
      </List>
    </Box>
  );
};

export default Sidebar;
