// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Box,
  TextField,
  Button,
  Snackbar,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [asset, setAsset] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Получаем текущего пользователя
  useEffect(() => {
    axios.get('http://localhost:5000/api/check-auth')
      .then(res => {
        setCurrentUser(res.data.user || null);
      })
      .catch(() => {
        setCurrentUser(null);
        navigate('/login');
      });

    // Загрузка данных актива
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        setSnackbar({
          open: true,
          message: 'Не удалось загрузить данные',
          severity: 'error'
        });
      });
  }, [id, navigate]);

  if (!asset) {
    return <Box p={3}>Загрузка...</Box>;
  }

  const handleChange = (e) => {
    setAsset({ ...asset, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false);
      setSnackbar({
        open: true,
        message: 'Данные успешно сохранены',
        severity: 'success'
      });
    } catch (err) {
      console.error('Ошибка сохранения:', err);
      setSnackbar({
        open: true,
        message: 'Не удалось сохранить изменения',
        severity: 'error'
      });
    }
  };

  const handlePrintQR = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head><title>Печать QR-кода</title></head>
        <body style="text-align:center;">
          <h3>${asset.inventory_number}</h3>
          ${document.getElementById('qrcode')?.outerHTML || ''}
          <script>window.print();</script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  const canEdit = currentUser && currentUser.role === 'admin';

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', backgroundColor: '#fff' }}>
        <Typography variant="h5" gutterBottom>Карточка оборудования</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!isEditing}
              size="small"
            />

            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              multiline
              rows={4}
              disabled={!isEditing}
              size="small"
            />

            {/* Статус — пример выпадающего списка */}
            {canEdit && isEditing && (
              <FormControl fullWidth margin="normal">
                <InputLabel id="status-label">Статус</InputLabel>
                <Select
                  labelId="status-label"
                  value={asset.status_id || ''}
                  label="Статус"
                  onChange={(e) =>
                    setAsset({ ...asset, status_id: e.target.value })
                  }
                >
                  <MenuItem value="1">В эксплуатации</MenuItem>
                  <MenuItem value="2">В ремонте</MenuItem>
                  <MenuItem value="3">Списано</MenuItem>
                  <MenuItem value="4">Перемещено</MenuItem>
                </Select>
              </FormControl>
            )}
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate('/')}>
            Назад к списку
          </Button>

          {!isEditing ? (
            canEdit ? (
              <Button variant="contained" color="primary" onClick={() => setIsEditing(true)}>
                Редактировать
              </Button>
            ) : (
              <Typography color="textSecondary" sx={{ alignSelf: 'center' }}>
                Только для просмотра
              </Typography>
            )
          ) : (
            <>
              <Button variant="outlined" color="secondary" onClick={() => setIsEditing(false)}>
                Отменить
              </Button>
              <Button variant="contained" color="success" onClick={handleSave}>
                Сохранить
              </Button>
            </>
          )}

          <Button variant="contained" color="info" onClick={handlePrintQR}>
            Печать QR-кода
          </Button>
        </Box>
      </Paper>

      {/* Snackbar для уведомлений */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default AssetDetails;
