// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Button,
} from '@mui/material';

const AssetList = ({ currentUser }) => {
  const [assets, setAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Загружаем активы из API
    axios.get('http://localhost:5000/api/assets')
      .then(res => {
        console.log('Received assets:', res.data); // Логируем полученные данные
        setAssets(res.data);
      })
      .catch(err => console.error('Error fetching assets:', err));
  }, []);

  const filteredAssets = assets.filter(asset =>
    asset.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.inventory_number.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>Список активов</Typography>
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '20px' }}
        />

        {/* Кнопка "Добавить актива" */}
        {currentUser && currentUser.role === 'admin' && (
          <Button
            variant="contained"
            color="primary"
            style={{ marginBottom: '20px' }}
            onClick={() => window.location.href = '/assets/new'}
          >
            Добавить актива
          </Button>
        )}

        <Table>
          <TableBody>
            {filteredAssets.map((asset) => (
              <TableRow key={asset.id}>
                <TableCell>{asset.serial_number}</TableCell>
                <TableCell>{asset.inventory_number}</TableCell>
                <TableCell>{asset.name}</TableCell>
                <TableCell>{asset.location}</TableCell>
                <TableCell>{asset.status_id}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
