// src/components/EmployeeList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assetCounts, setAssetCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем сотрудников
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const emps = empsRes.data;
        setEmployees(emps);

        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Фамилия</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Отчество</TableCell>
              <TableCell>Должность</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Помещение</TableCell>
              <TableCell>Вн. телефон</TableCell>
              <TableCell align="right">Оборудования</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {employees.map(emp => {
              const dept = departments.find(d => d.id === emp.department_id);

              // Разбиваем ФИО на фронтенде
              const fullName = emp.name.split(' ');
              const surname = fullName[0] || '';
              const name = fullName[1] || '';
              const patronymic = fullName[2] || '';

              return (
                <TableRow key={emp.id}>
                  <TableCell>{surname}</TableCell>
                  <TableCell>{name}</TableCell>
                  <TableCell>{patronymic || '-'}</TableCell>
                  <TableCell>-</TableCell>
                  <TableCell>{dept?.name || 'Не указано'}</TableCell>
                  <TableCell>{emp.room || '-'}</TableCell>
                  <TableCell>{emp.internal_phone || '-'}</TableCell>
                  <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default EmployeeList; // Экспорт по умолчанию
