// frontend/src/components/AssetDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
} from '@mui/material';
import axios from 'axios';
import { API_BASE_URL } from '../config';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type_id: '',
    brand: '',
    model: '',
    status_id: '',
    actual_user: '',
    responsible_person: '',
    department_id: '',
    room: '',
    purchase_date: '',
    comments: '',
    diagonal: '',
    CPU: '',
    RAM: '',
    Drive: '',
    OS: '',
    IP_address: '',
    number: ''
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState('');
  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [typeProperties, setTypeProperties] = useState([]);
  const [dynamicFields, setDynamicFields] = useState([]); // ✅ массив, не объект
  const [errors, setErrors] = useState({});

  // Загрузка данных
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [assetRes, typesRes, statusesRes, employeesRes, departmentsRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
        ]);

        setAsset({
          ...assetRes.data,
          diagonal: assetRes.data.diagonal || '',
          CPU: assetRes.data.CPU || '',
          RAM: assetRes.data.RAM || '',
          Drive: assetRes.data.Drive || '',
          OS: assetRes.data.OS || '',
          IP_address: assetRes.data.IP_address || '',
          number: assetRes.data.number || ''
        });
        setTypes(typesRes.data);
        setStatuses(statusesRes.data);
        setEmployees(employeesRes.data);
        setDepartments(departmentsRes.data);

        // Устанавливаем URL QR-кода
        setQrCodeUrl(`${API_BASE_URL}/api/assets/${id}/qr`);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  // Загрузка свойств типа при изменении type_id
  useEffect(() => {
    const fetchTypeProperties = async () => {
      if (!asset.type_id) {
        setTypeProperties([]);
        setDynamicFields([]);
        return;
      }

      try {
        const res = await axios.get(`${API_BASE_URL}/api/types/${asset.type_id}/properties`, { withCredentials: true });
        setTypeProperties(res.data);

        const fields = res.data.map(prop => {
          const fieldName = mapPropertyNameToAssetField(prop.name);
          return {
            id: prop.id,
            name: prop.name,
            fieldName: fieldName,
            value: asset[fieldName] || ''
          };
        });
        setDynamicFields(fields);
      } catch (err) {
        console.error('Ошибка при загрузке свойств типа:', err);
        setTypeProperties([]);
        setDynamicFields([]);
      }
    };

    fetchTypeProperties();
  }, [asset.type_id]); // ✅ Зависимость от type_id

  // Маппинг названий свойств на поля
  const mapPropertyNameToAssetField = (propertyName) => {
    const mapping = {
      'Диагональ': 'diagonal',
      'Диагональ (дюймы)': 'diagonal',
      'Процессор': 'CPU',
      'Оперативная память': 'RAM',
      'ОЗУ': 'RAM',
      'Диск': 'Drive',
      'ОС': 'OS',
      'Операционная система': 'OS',
      'IP-адрес': 'IP_address',
      'Внутренний номер': 'number'
    };
    return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_');
  };

  // Обработчик изменения динамических полей
  const handleDynamicFieldChange = (fieldName, value) => {
    setAsset(prev => ({ ...prev, [fieldName]: value }));
    setDynamicFields(prev =>
      prev.map(f => f.fieldName === fieldName ? { ...f, value } : f)
    );
  };

  // Рендер динамических полей
  const renderDynamicField = (field) => (
    <Grid item xs={12} sm={6} key={field.id}>
      <TextField
        label={field.name}
        name={field.fieldName}
        value={field.value}
        onChange={(e) => handleDynamicFieldChange(field.fieldName, e.target.value)}
        fullWidth
        margin="normal"
        disabled={!currentUser || currentUser.role !== 'admin'}
      />
    </Grid>
  );

  // Обработчик изменения обычных полей
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset(prev => ({ ...prev, [name]: value }));
  };

  // Сохранение изменений
  const handleSave = async () => {
    const dataToSend = {
      serial_number: asset.serial_number,
      inventory_number: asset.inventory_number,
      type_id: asset.type_id,
      brand: asset.brand,
      model: asset.model,
      status_id: asset.status_id,
      actual_user: asset.actual_user,
      responsible_person: asset.responsible_person,
      department_id: asset.department_id,
      room: asset.room,
      purchase_date: asset.purchase_date,
      comments: asset.comments,
      diagonal: asset.diagonal,
      CPU: asset.CPU,
      RAM: asset.RAM,
      Drive: asset.Drive,
      OS: asset.OS,
      IP_address: asset.IP_address,
      number: asset.number
    };

    try {
      await axios.put(`${API_BASE_URL}/api/assets/${id}`, dataToSend, { withCredentials: true });
      alert('Данные успешно обновлены');
    } catch (err) {
      console.error('Ошибка при сохранении:', err);
      const message = err.response?.data?.error || 'Не удалось сохранить изменения';
      alert(message);
    }
  };

  // Удаление актива
  const handleDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true });
      alert('Актив успешно удалён');
      navigate('/assets');
    } catch (err) {
      console.error('Ошибка при удалении:', err);
      setDeleteError('Не удалось удалить актив. Возможно, он используется.');
    }
  };

  if (loading) return <CircularProgress />;
  if (error) return <Alert severity="error">{error}</Alert>;

  return (
    <Container maxWidth="lg" style={{ marginTop: '20px' }}>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4">Карточка актива</Typography>
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={8}>
          <Box mb={3}>
            <Typography variant="h6">
              {types.find(t => t.id === asset.type_id)?.name || '—'} {asset.brand} {asset.model}
            </Typography>
          </Box>

          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField label="Серийный номер" name="serial_number" value={asset.serial_number} onChange={handleChange} fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField label="Инвентарный номер" name="inventory_number" value={asset.inventory_number} onChange={handleChange} fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
                <InputLabel>Тип</InputLabel>
                <Select name="type_id" value={asset.type_id} onChange={handleChange} label="Тип">
                  {types.map(t => <MenuItem key={t.id} value={t.id}>{t.name}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField label="Фирма" name="brand" value={asset.brand} onChange={handleChange} fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField label="Модель" name="model" value={asset.model} onChange={handleChange} fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
                <InputLabel>Статус</InputLabel>
                <Select name="status_id" value={asset.status_id} onChange={handleChange} label="Статус">
                  {statuses.map(s => <MenuItem key={s.id} value={s.id}>{s.name}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
                <InputLabel>Фактический пользователь</InputLabel>
                <Select name="actual_user" value={asset.actual_user} onChange={handleChange} label="Фактический пользователь">
                  {employees.map(e => <MenuItem key={e.id} value={e.id}>{e.name}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
                <InputLabel>Ответственный</InputLabel>
                <Select name="responsible_person" value={asset.responsible_person} onChange={handleChange} label="Ответственный">
                  {employees.map(e => <MenuItem key={e.id} value={e.id}>{e.name}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
                <InputLabel>Отдел</InputLabel>
                <Select name="department_id" value={asset.department_id} onChange={handleChange} label="Отдел">
                  {departments.map(d => <MenuItem key={d.id} value={d.id}>{d.name}</MenuItem>)}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField label="Помещение" name="room" value={asset.room} onChange={handleChange} fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'} />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Дата покупки"
                name="purchase_date"
                type="date"
                value={asset.purchase_date}
                onChange={handleChange}
                fullWidth
                margin="normal"
                InputLabelProps={{ shrink: true }}
                disabled={!currentUser || currentUser.role !== 'admin'}
              />
            </Grid>

            {/* Динамические поля */}
            {dynamicFields.map(renderDynamicField)}

            <Grid item xs={12}>
              <TextField
                label="Комментарий"
                name="comments"
                value={asset.comments}
                onChange={handleChange}
                fullWidth
                multiline
                rows={3}
                margin="normal"
                disabled={!currentUser || currentUser.role !== 'admin'}
              />
            </Grid>
          </Grid>

          {currentUser && currentUser.role === 'admin' && (
            <Box mt={3}>
              <Button variant="contained" color="primary" onClick={handleSave}>Сохранить изменения</Button>
            </Box>
          )}
        </Grid>

        <Grid item xs={12} md={4}>
          <Box mb={3}>
            <Typography variant="h6">QR-код</Typography>
            {qrCodeUrl ? (
              <img src={qrCodeUrl} alt="QR-код" style={{ maxWidth: '100%', border: '1px solid #ddd', borderRadius: 4 }} />
            ) : (
              <Typography>QR-код недоступен</Typography>
            )}
          </Box>

          <Box>
            <Typography variant="h6">Действия</Typography>
            <Button
              variant="contained"
              color="secondary"
              onClick={() => setOpenDeleteDialog(true)}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              style={{ marginBottom: '10px' }}
            >
              Удалить актив
            </Button>
            <Button variant="outlined" onClick={() => navigate('/assets')} fullWidth>Назад к списку</Button>
          </Box>
        </Grid>
      </Grid>

      <Dialog open={openDeleteDialog} onClose={() => setOpenDeleteDialog(false)}>
        <DialogTitle>Подтвердить удаление</DialogTitle>
        <DialogContent>
          <DialogContentText>Вы действительно хотите удалить этот актив?</DialogContentText>
          {deleteError && <Alert severity="error" style={{ marginTop: '10px' }}>{deleteError}</Alert>}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Отмена</Button>
          <Button onClick={handleDelete} color="secondary">Удалить</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AssetDetails;
