// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type: '',         // Новое поле
    brand: '',        // Обязательное
    model: '',        // Обязательное
    category_id: '',
    department_id: '',     // Отдел
    room: '',              // Помещение
    purchase_date: '',     // Дата покупки
    responsible_person: '', // Теперь это ID сотрудника
    actual_user: '',
    comments: '',
    status_id: ''
  });
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [errors, setErrors] = useState({});
  
  // Загружаем актив по ID при монтировании
  useEffect(() => {
    if (!id || id === 'new') return;
    
    axios.get(`http://localhost:5000/api/assets/${id}`)
    .then(res => {
      setAsset(res.data);
    })
    .catch(err => {
      console.error('Ошибка загрузки актива:', err);
      alert('Не удалось загрузить актив');
      navigate('/assets');
    });
    
    // Загружаем справочные данные
    axios.get('http://localhost:5000/api/categories')
    .then(res => setCategories(res.data))
    .catch(err => console.error('Ошибка загрузки категорий:', err));
    
    axios.get('http://localhost:5000/api/statuses')
    .then(res => setStatuses(res.data))
    .catch(err => console.error('Ошибка загрузки статусов:', err));
    
    axios.get('http://localhost:5000/api/departments')
    .then(res => setDepartments(res.data))
    .catch(err => console.error('Ошибка загрузки отделов:', err));
    
    axios.get('http://localhost:5000/api/employees')
    .then(res => setEmployees(res.data))
    .catch(err => console.error('Ошибка загрузки сотрудников:', err));
  }, [id]);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset({ ...asset, [name]: value });
  };
  
  const handleResponsiblePersonChange = async (e) => {
    const selectedEmployeeId = e.target.value;
    
    try {
      // Получаем отдел сотрудника
      const res = await axios.get(`http://localhost:5000/api/employees/${selectedEmployeeId}`);
      const departmentId = res.data.department_id;
      
      // Обновляем поля "Ответственный" и "Отдел"
      setAsset({
        ...asset,
        responsible_person: selectedEmployeeId,
        department_id: departmentId,
      });
    } catch (err) {
      console.error('Ошибка при получении отдела сотрудника:', err);
      alert('Не удалось получить отдел сотрудника');
    }
  };
  
  
  
  const handleSave = async () => {
    // Проверка обязательных полей
    const newErrors = {};
    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.category_id) newErrors.category_id = 'Обязательное поле';
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!asset.responsible_person) newErrors.responsible_person = 'Обязательное поле'; // ← Новое правило
    if (!asset.purchase_date) newErrors.purchase_date = 'Обязательное поле';


    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно обновлены');
    } catch (err) {
      alert('Ошибка при сохранении изменений');
      console.error(err);
    }
  };

  useEffect(() => {
  if (asset && asset.id) { // Убедимся, что asset загружен и у него есть id
    // Формируем URL для запроса QR-кода с бэкенда
    const qrUrl = `http://localhost:5000/api/assets/${asset.id}/qr`; // Замените на ваш REACT_APP_API_URL
    setQrCodeUrl(qrUrl);
  }
}, [asset]); // Зависимость от asset

  if (!asset) {
    return (
      <Container maxWidth="md">
        <Paper elevation={3} style={{ padding: '20px' }}>
          <Typography variant="h5">Актив не найден</Typography>
          <Button variant="outlined" onClick={() => navigate('/assets')}>
            Назад
          </Button>
        </Paper>
      </Container>
    );
  }


  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        {/* Шапка с полным названием */}
        <Typography variant="h5" gutterBottom>
          {asset.full_name}
        </Typography>

      <Grid container spacing={2}>
        {/* Пара 1: Серийный номер | Инвентарный номер */}
        <Grid item xs={6}>
          <TextField
            label="Серийный номер"
            name="serial_number"
            value={asset.serial_number || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Инвентарный номер *"
            name="inventory_number"
            value={asset.inventory_number || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            error={!!errors.inventory_number}
            helperText={errors.inventory_number}
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>

        {/* Пара 2: Категория | Фирма */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.category_id}>
            <InputLabel id="category-label">Категория *</InputLabel>
            <Select
              labelId="category-label"
              label="Категория *"
              name="category_id"
              value={asset.category_id || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              {categories.map(cat => (
                <MenuItem key={cat.id} value={cat.id}>
                  {cat.name}
                </MenuItem>
              ))}
            </Select>
            {errors.category_id && (
              <Typography color="error" variant="caption">
                {errors.category_id}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Фирма"
            name="brand"
            value={asset.brand || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>

        {/* Пара 3: Модель | Конфигурация */}
        <Grid item xs={6}>
          <TextField
            label="Модель"
            name="model"
            value={asset.model || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Конфигурация (Процессор/DDR/HDD)"
            name="type"
            value={asset.type || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>

        {/* Пара 4: Статус | Фактический пользователь */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.status_id}>
            <InputLabel id="status-label">Статус *</InputLabel>
            <Select
              labelId="status-label"
              label="Статус *"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              {statuses.map(stat => (
                <MenuItem key={stat.id} value={stat.id}>
                  {stat.name}
                </MenuItem>
              ))}
            </Select>
            {errors.status_id && (
              <Typography color="error" variant="caption">
                {errors.status_id}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Фактический пользователь"
            name="actual_user"
            value={asset.actual_user || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>

        {/* Пара 5: Ответственный | Отдел (подразделение) */}
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal" error={!!errors.responsible_person}>
            <InputLabel id="employee-label">Ответственный *</InputLabel>
            <Select
              labelId="employee-label"
              label="Ответственный *"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleResponsiblePersonChange}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              {employees.map(emp => (
                <MenuItem key={emp.id} value={emp.id}>
                  {emp.name}
                </MenuItem>
              ))}
            </Select>
            {errors.responsible_person && (
              <Typography color="error" variant="caption">
                {errors.responsible_person}
              </Typography>
            )}
          </FormControl>
        </Grid>
        <Grid item xs={6}>
          <FormControl fullWidth margin="normal">
            <InputLabel id="department-label">Отдел (подразделение)</InputLabel>
            <Select
              labelId="department-label"
              label="Отдел (подразделение)"
              name="department_id"
              value={asset.department_id || ''}
              onChange={handleChange}
              disabled // Поле всегда заблокировано для редактирования
              style={{ 
                backgroundColor: '#f5f5f5',
                color: 'rgba(0, 0, 0, 0.38)'
              }}
            >
              {departments.map(dept => (
                <MenuItem key={dept.id} value={dept.id}>
                  {dept.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>

        {/* Пара 6: Помещение | Дата покупки */}
        <Grid item xs={6}>
          <TextField
            label="Помещение"
            name="room"
            value={asset.room || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Дата покупки *"
            name="purchase_date"
            type="date"
            value={asset.purchase_date || ''}
            onChange={handleChange}
            fullWidth
            margin="normal"
            error={!!errors.purchase_date}
            helperText={errors.purchase_date}
            InputLabelProps={{
              shrink: true,
            }}
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>

        {/* Комментарий на всю ширину */}
        <Grid item xs={12}>
          <TextField
            label="Комментарий"
            name="comments"
            value={asset.comments || ''}
            onChange={handleChange}
            fullWidth
            multiline
            rows={4}
            margin="normal"
            disabled={!currentUser || currentUser.role !== 'admin'}
          />
        </Grid>
      </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={`http://localhost:3000/assets/${asset.id}`}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопка печати QR-кода */}
        <Box mt={0.5} display="flex" justifyContent="center">
          <Button variant="contained" color="primary" onClick={handlePrintQRCode}>
            Печать QR
          </Button>
        </Box>

        {/* Кнопки управления */}
        <Box mt={4.5} display="flex" justifyContent="space-between" alignItems="center">
          <Button variant="outlined" onClick={() => navigate('/assets')}>
            Назад
          </Button>
          <Box>
            {currentUser && currentUser.role === 'admin' && (
              <>
                <Button
                  variant="contained"
                  color="error"
                  onClick={() => {
                    if (!window.confirm('Вы уверены, что хотите удалить этот актив?')) return;
                    try {
                      axios.delete(`http://localhost:5000/api/assets/${id}`);
                      alert('Актив успешно удален');
                      navigate('/assets');
                    } catch (err) {
                      alert('Ошибка при удалении актива');
                      console.error(err);
                    }
                  }}
                  sx={{ mr: '8px' }}
                >
                  Удалить
                </Button>
                <Button
                  variant="contained"
                  color="success"
                  onClick={handleSave}
                >
                  Сохранить
                </Button>
              </>
            )}
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
