// frontend/src/components/Login.js
import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  TextField,
  Button,
  Typography,
  Paper
} from '@mui/material';

const Login = ({ onLogin, onClose }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post(
        'http://localhost:5000/api/login',
        { username, password },
        { withCredentials: true }
      );

      if (response.status === 200 && response.data.role === 'admin') {
        onLogin({ username: response.data.username, role: response.data.role });
        onClose(); // Закрыть модальное окно после входа
      } else {
        setError('Только администратор может войти');
      }
    } catch (err) {
      setError('Ошибка при входе: неверный логин или пароль');
      console.error(err);
    }
  };

  return (
    <Paper sx={{ p: 3, width: 300 }}>
      <Typography variant="h6">Вход</Typography>
      {error && <Typography color="error">{error}</Typography>}

      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
        <TextField
          label="Логин"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <TextField
          label="Пароль"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <Button variant="contained" color="primary" type="submit">
          Войти
        </Button>

        <Button onClick={onClose} color="secondary">
          Отмена
        </Button>
      </Box>
    </Paper>
  );
};

export default Login;
