// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  TextField,
  Button,
} from '@mui/material';

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]); // Добавляем состояние для отделов
  const [categories, setCategories] = useState([]);

  const [searchTerm, setSearchTerm] = useState('');

  // Загружаем все данные
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const employeesRes = await axios.get('http://localhost:5000/api/employees');
        const statusesRes = await axios.get('http://localhost:5000/api/statuses');

        setAssets(assetsRes.data);
        setEmployees(employeesRes.data);
        setStatuses(statusesRes.data);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  // Фильтруем активы по поиску
  const filteredAssets = assets.filter(asset =>
    asset.inventory_number?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    asset.name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Вспомогательная функция для форматирования ФИО
  const formatResponsiblePerson = (name) => {
    if (!name) return '';

    const parts = name.split(' ');
    const surname = parts[0] || '';
    const firstNameInitial = parts[1]?.charAt(0) + '.' || '';
    const patronymicInitial = parts[2]?.charAt(0) + '.' || '';

    return `${surname} ${firstNameInitial}${patronymicInitial}`;
  };

  // Получаем статус по ID
  const getStatusName = (id) => {
    const status = statuses.find(stat => stat.id === id);
    return status?.name || 'Не указано';
  };

  // Получаем имя отдела по ID
  const getDepartmentName = (department_id, departments) => {
    const dept = departments.find(d => d.id === department_id);
    return dept?.name || 'Не указано';
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Список оборудования
        </Typography>

        {/* Кнопка добавления актива */}
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/assets/new')}
          style={{ marginBottom: '16px' }}
        >
          Добавить новый актив
        </Button>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Инвентарный номер</TableCell>
              <TableCell>Наименование</TableCell>
              <TableCell>Статус</TableCell>
              <TableCell>Ответственный</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Дата покупки</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {assets.map(asset => (
              <TableRow key={asset.id}>
                <TableCell>{asset.inventory_number}</TableCell>
                <TableCell>{asset.name}</TableCell>
                <TableCell>{getStatusName(asset.status_id)}</TableCell>
                <TableCell>{getResponsiblePerson(asset.responsible_person)}</TableCell>
                <TableCell>{getDepartmentName(asset.department_id, departments)}</TableCell>
                <TableCell>{asset.purchase_date ? new Date(asset.purchase_date).toLocaleDateString() : '-'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>


        <TextField
          label="Поиск"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          fullWidth
          margin="normal"
        />

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Инвентарный номер</TableCell>
              <TableCell>Наименование</TableCell>
              <TableCell>Статус</TableCell>
              <TableCell>Ответственный</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Дата покупки</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredAssets.map(asset => {
              const responsiblePerson = formatResponsiblePerson(asset.responsible_person);

              return (
                <TableRow key={asset.id}>
                  <TableCell>{asset.inventory_number}</TableCell>
                  <TableCell>{asset.type} {asset.brand} {asset.model}</TableCell>
                  <TableCell>{getStatusName(asset.status_id)}</TableCell>
                  <TableCell>{responsiblePerson}</TableCell>
                  <TableCell>{getDepartmentName(asset.department_id, departments)}</TableCell>
                  <TableCell>{asset.purchase_date ? new Date(asset.purchase_date).toLocaleDateString() : '-'}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
