// frontend/src/components/AssetDetails.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress,
  Grid,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Paper
} from '@mui/material';
// Импорты иконок удалены, так как они больше не используются
// import ArrowBackIcon from '@mui/icons-material/ArrowBack';
// import DeleteIcon from '@mui/icons-material/Delete';
// import EditIcon from '@mui/icons-material/Edit';
// import PrintIcon from '@mui/icons-material/Print';
import axios from 'axios';
import { API_BASE_URL } from '../config'; // Импорт базового URL

const AssetDetails = ({ currentUser }) => { // Принимаем currentUser как пропс
  const { id } = useParams();
  const navigate = useNavigate();
  const [asset, setAsset] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);
  const [deleteError, setDeleteError] = useState('');
  const [qrCodeUrl, setQrCodeUrl] = useState(''); // Состояние для URL QR-кода

  // Состояния для редактирования
  const [isEditing, setIsEditing] = useState(false);
  const [editedAsset, setEditedAsset] = useState({});
  const [editErrors, setEditErrors] = useState({});

  // Состояния для выпадающих списков
  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  // Загрузка данных актива и справочников
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError('');
      try {
        const [assetRes, typesRes, statusesRes, departmentsRes, employeesRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
        ]);

        setAsset(assetRes.data);
        setEditedAsset(assetRes.data); // Инициализируем данные для редактирования
        setTypes(typesRes.data);
        setStatuses(statusesRes.data);
        setDepartments(departmentsRes.data);
        setEmployees(employeesRes.data);

        // Формируем URL для QR-кода
        setQrCodeUrl(`${API_BASE_URL}/api/assets/${id}/qr`);

      } catch (err) {
        console.error('Ошибка при загрузке актива:', err);
        setError('Не удалось загрузить данные актива.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [id]);

  const handleDelete = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/api/assets/${id}`, { withCredentials: true });
      navigate('/assets');
    } catch (err) {
      console.error('Ошибка при удалении актива:', err);
      setDeleteError('Не удалось удалить актив.');
    }
  };

  const handleOpenDeleteDialog = () => {
    setOpenDeleteDialog(true);
  };

  const handleCloseDeleteDialog = () => {
    setOpenDeleteDialog(false);
    setDeleteError('');
  };

  const handlePrintQRCode = () => {
    if (qrCodeUrl) {
      // Открываем URL QR-кода в новом окне и вызываем печать
      const printWindow = window.open(qrCodeUrl, '_blank');
      printWindow.onload = () => {
        printWindow.print();
      };
    }
  };

  // --- Логика редактирования ---
  const handleEditToggle = () => {
    if (isEditing) {
      // Отмена редактирования - сброс к исходным данным
      setEditedAsset(asset);
      setEditErrors({});
    }
    setIsEditing(!isEditing);
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditedAsset((prev) => ({ ...prev, [name]: value }));

    // Очистка ошибок при изменении поля
    if (editErrors[name]) {
      setEditErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  const handleSave = async () => {
    // Простая валидация на фронтенде (можно расширить)
    const newErrors = {};
    if (!editedAsset.inventory_number) newErrors.inventory_number = 'Обязательное поле';
    if (!editedAsset.type_id) newErrors.type_id = 'Обязательное поле';
    if (!editedAsset.status_id) newErrors.status_id = 'Обязательное поле';
    // Добавим валидацию для других обязательных полей, если они есть
    // Например, если department_id и purchase_date обязательны:
    // if (!editedAsset.department_id) newErrors.department_id = 'Обязательное поле';
    // if (!editedAsset.purchase_date) newErrors.purchase_date = 'Обязательное поле';

    if (Object.keys(newErrors).length > 0) {
      setEditErrors(newErrors);
      return;
    }

    try {
      const response = await axios.put(`${API_BASE_URL}/api/assets/${id}`, editedAsset, { withCredentials: true });
      setAsset(response.data); // Обновляем основной объект актива
      setIsEditing(false);
      setEditErrors({});
      alert('Актив успешно обновлён');
    } catch (err) {
      console.error('Ошибка при обновлении актива:', err);
      alert('Ошибка при обновлении актива: ' + (err.response?.data?.error || err.message));
    }
  };

  if (loading) return (
    <Container maxWidth="md" style={{ marginTop: '20px', textAlign: 'center' }}>
      <CircularProgress />
      <Typography>Загрузка...</Typography>
    </Container>
  );
  if (error) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="error">{error}</Alert>
    </Container>
  );
  if (!asset) return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Alert severity="warning">Актив не найден</Alert>
    </Container>
  );

  // Определяем, является ли пользователь администратором
  const isAdmin = currentUser && currentUser.role === 'admin';
  // Определяем, должны ли поля быть отключены
  const isDisabled = !isAdmin || !isEditing; // Отключено, если не админ ИЛИ не в режиме редактирования

  return (
    <Container maxWidth="lg" style={{ marginTop: '20px' }}>
      {/* <<<--- ИЗМЕНЕНО: Кнопка "Назад" перемещена сюда и без иконки */}
      <Button
        // startIcon={<ArrowBackIcon />} // Иконка удалена
        onClick={() => navigate('/assets')}
        variant="outlined"
        style={{ marginBottom: '20px' }}
      >
        Назад к списку
      </Button>

      <Card>
        <CardContent maxWidth="md" style={{ marginTop: '10px' }}>
          <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
            <Typography variant="h5" gutterBottom> {asset.category_name} {asset.brand} {asset.model} </Typography>

            <Grid container spacing={2}>
              {/* Левая колонка */}
              <Grid item xs={12} md={6}>
                <TextField
                  label="Серийный номер"
                  name="serial_number"
                  value={isEditing ? editedAsset.serial_number : asset.serial_number}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}

                />
                <TextField
                  label="Инвентарный номер *"
                  name="inventory_number"
                  value={isEditing ? editedAsset.inventory_number : asset.inventory_number}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!(isEditing ? editErrors.inventory_number : null)}
                  helperText={isEditing ? editErrors.inventory_number : null}
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
                <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.type_id : null)}>
                  <InputLabel id="edit-type-label">Тип устройства *</InputLabel>
                  <Select
                    labelId="edit-type-label"
                    label="Тип устройства *"
                    name="type_id"
                    value={isEditing ? editedAsset.type_id : asset.type_id}
                    onChange={handleEditChange}
                    disabled={!currentUser || currentUser.role !== 'admin'}
                  >
                    {types.map(cat => (
                      <MenuItem key={cat.id} value={cat.id}>
                        {cat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {isEditing && editErrors.type_id && (
                    <FormHelperText>{editErrors.type_id}</FormHelperText>
                  )}
                </FormControl>
                <TextField
                  label="Фирма"
                  name="brand"
                  value={isEditing ? editedAsset.brand : asset.brand}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
                <TextField
                  label="Модель"
                  name="model"
                  value={isEditing ? editedAsset.model : asset.model}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
                <TextField
                  label="Конфигурация (Процессор/DDR/HDD)"
                  name="type"
                  value={isEditing ? editedAsset.type : asset.type}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
              </Grid>

              {/* Правая колонка */}
              <Grid item xs={12} md={6}>
                <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.status_id : null)}>
                  <InputLabel id="edit-status-label">Статус *</InputLabel>
                  <Select
                    labelId="edit-status-label"
                    label="Статус *"
                    name="status_id"
                    value={isEditing ? editedAsset.status_id : asset.status_id}
                    onChange={handleEditChange}
                    disabled={!currentUser || currentUser.role !== 'admin'}
                  >
                    {statuses.map(stat => (
                      <MenuItem key={stat.id} value={stat.id}>
                        {stat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {isEditing && editErrors.status_id && (
                    <FormHelperText>{editErrors.status_id}</FormHelperText>
                  )}
                </FormControl>
                <TextField
                  label="Фактический пользователь"
                  name="actual_user"
                  value={isEditing ? editedAsset.actual_user : asset.actual_user}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
                <FormControl fullWidth margin="normal" error={!!(isEditing ? editErrors.responsible_person : null)}>
                  <InputLabel id="edit-responsible-person-label">Ответственный *</InputLabel>
                  <Select
                    labelId="edit-responsible-person-label"
                    label="Ответственный *"
                    name="responsible_person"
                    value={isEditing ? editedAsset.responsible_person : asset.responsible_person}
                    onChange={handleEditChange}
                    disabled={!currentUser || currentUser.role !== 'admin'}
                  >
                    {employees.map(emp => (
                      <MenuItem key={emp.id} value={emp.id}>
                        {emp.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {isEditing && editErrors.responsible_person && (
                    <FormHelperText>{editErrors.responsible_person}</FormHelperText>
                  )}
                </FormControl>
                <FormControl fullWidth margin="normal">
                  <InputLabel id="edit-department-label">Отдел (подразделение)</InputLabel>
                  <Select
                    labelId="edit-department-label"
                    label="Отдел (подразделение)"
                    name="department_id"
                    value={isEditing ? editedAsset.department_id : asset.department_id}
                    onChange={handleEditChange}
                    disabled
                    style={{
                      backgroundColor: '#f5f5f5',
                      color: 'rgba(0, 0, 0, 0.87)', // Цвет текста сделан более контрастным
                      cursor: 'not-allowed'
                    }}
                    sx={{
                      mb: 0,
                      '& .MuiOutlinedInput-notchedOutline': { borderColor: 'rgba(0, 0, 0, 0.23)' },
                      '& .MuiSelect-select': { color: 'rgba(0, 0, 0, 0.87)' }
                    }}
                  >
                    {departments.map(dept => (
                      <MenuItem key={dept.id} value={dept.id}>
                        {dept.name}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
                <TextField
                  label="Помещение"
                  name="room"
                  value={isEditing ? editedAsset.room : asset.room}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
                <TextField
                  label="Дата покупки *"
                  name="purchase_date"
                  type="date"
                  value={isEditing ? editedAsset.purchase_date : asset.purchase_date}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!(isEditing ? editErrors.purchase_date : null)}
                  helperText={isEditing ? editErrors.purchase_date : null}
                  InputLabelProps={{
                    shrink: true,
                  }}
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
              </Grid>

              {/* Комментарий на всю ширину */}
              <Grid item xs={12}>
                <TextField
                  label="Комментарий"
                  name="comments"
                  value={isEditing ? editedAsset.comments : asset.comments}
                  onChange={handleEditChange}
                  fullWidth
                  multiline
                  rows={4}
                  margin="normal"
                  disabled={!currentUser || currentUser.role !== 'admin'}
                />
              </Grid>

              {/* <<<--- ИЗМЕНЕНО: QR-код в той же карточке, без заголовка */}
              <Grid item xs={12} display="flex" justifyContent="center" mt={2}>
                {qrCodeUrl ? (
                  <img
                    src={qrCodeUrl}
                    alt={`QR-код для актива ${asset.inventory_number}`}
                    style={{ maxWidth: '200px', height: 'auto' }}
                  />
                ) : (
                  <Typography>Загрузка QR-кода...</Typography>
                )}
              </Grid>
            </Grid>
          </Box>
        </CardContent>

        <Box display="flex" justifyContent="space-between" p={2}>
          <Box>
            <Button
              onClick={() => navigate('/assets')}
              variant="outlined"
            >
              Назад к списку
            </Button>
          </Box>

          {/* Правая часть - кнопки "Удалить" и "Сохранить"/"Редактировать" */}
          <Box>
            {/* <<<--- ИЗМЕНЕНО: Кнопки только для администраторов */}
            {isAdmin && (
              <>
                {!isEditing ? (
                  // Режим просмотра - показываем "Редактировать" и "Удалить"
                  <>
                    <Button
                      // variant="outlined"
                      // color="primary"
                      // startIcon={<EditIcon />} // Иконка удалена
                      onClick={handleEditToggle}
                      style={{ marginRight: '8px' }}
                    >
                      Редактировать
                    </Button>
                    <Button
                      // variant="outlined"
                      // color="error"
                      // startIcon={<DeleteIcon />} // Иконка удалена
                      onClick={handleOpenDeleteDialog}
                    >
                      Удалить
                    </Button>
                  </>
                ) : (
                  // Режим редактирования - показываем "Отмена" и "Сохранить"
                  <>
                    <Button
                      // variant="outlined"
                      // color="secondary"
                      onClick={handleEditToggle}
                      style={{ marginRight: '8px' }}
                    >
                      Отмена
                    </Button>
                    <Button
                      // variant="contained"
                      // color="primary"
                      onClick={handleSave}
                    >
                      Сохранить
                    </Button>
                  </>
                )}
              </>
            )}
          </Box>
        </Box>
      </Card>

      {/* Диалог подтверждения удаления */}
      <Dialog
        open={openDeleteDialog}
        onClose={handleCloseDeleteDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Подтвердите удаление"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите удалить актив "{asset.inventory_number}"?
            Это действие нельзя отменить.
          </DialogContentText>
          {deleteError && (
            <Alert severity="error" style={{ marginTop: '10px' }}>
              {deleteError}
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDeleteDialog} color="primary">
            Отмена
          </Button>
          <Button onClick={handleDelete} color="error" autoFocus>
            Удалить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AssetDetails;
