import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';

const AssetDetails = () => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type: '',
    brand: '',
    model: '',
    category_id: null,
    department_id: null,
    room: '',
    purchase_date: null,
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: null,
  });
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    const fetchAsset = async () => {
      if (id) {
        try {
          const res = await axios.get(`http://localhost:5000/api/assets/${id}`);
          setAsset(res.data);
        } catch (err) {
          console.error('Ошибка при загрузке актива:', err);
        }
      }
    };

    const fetchEmployees = async () => {
      try {
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        setEmployees(empsRes.data);
      } catch (err) {
        console.error('Ошибка при загрузке сотрудников:', err);
      }
    };

    const fetchDepartments = async () => {
      try {
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        setDepartments(depsRes.data);
      } catch (err) {
        console.error('Ошибка при загрузке отделов:', err);
      }
    };

    fetchAsset();
    fetchEmployees();
    fetchDepartments();
  }, [id]);

  const handleChange = (event) => {
    setAsset({ ...asset, [event.target.name]: event.target.value });
  };

  const handleResponsiblePersonChange = async (event) => {
    const selectedEmployeeId = event.target.value;

    // Обновляем поле "Ответственный"
    setAsset({ ...asset, responsible_person: selectedEmployeeId });

    // Получаем отдел сотрудника
    try {
      const res = await axios.get(`http://localhost:5000/api/employees/${selectedEmployeeId}`);
      const departmentId = res.data.department_id;
      setAsset({ ...asset, department_id: departmentId });
    } catch (err) {
      console.error('Ошибка при получении отдела сотрудника:', err);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно обновлены');
    } catch (err) {
      alert('Ошибка при сохранении изменений');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Редактировать актив: {asset.type} {asset.brand} {asset.model}
        </Typography>

        <Grid container spacing={2}>
          {/* Левая колонка */}
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
            />
            <TextField
              label="Тип *"
              name="type"
              value={asset.type || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.type}
              helperText={errors.type}
            />
            <TextField
              label="Бренд"
              name="brand"
              value={asset.brand || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Модель"
              name="model"
              value={asset.model || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="category-label">Категория *</InputLabel>
              <Select
                labelId="category-label"
                label="Категория *"
                name="category_id"
                value={asset.category_id || ''}
                onChange={handleChange}
              >
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          {/* Правая колонка */}
          <Grid item xs={6}>
            <TextField
              label="Местоположение"
              name="room"
              value={asset.room || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Дата покупки"
              name="purchase_date"
              type="date"
              value={asset.purchase_date || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="employee-label">Ответственный</InputLabel>
              <Select
                labelId="employee-label"
                label="Ответственный"
                name="responsible_person"
                value={asset.responsible_person || ''}
                onChange={handleResponsiblePersonChange}
              >
                {employees.map(emp => (
                  <MenuItem key={emp.id} value={emp.id}>
                    {emp.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl fullWidth margin="normal">
              <InputLabel id="department-label">Отдел</InputLabel>
              <Select
                labelId="department-label"
                label="Отдел"
                name="department_id"
                value={asset.department_id || ''}
                onChange={handleChange}
              >
                {departments.map(dept => (
                  <MenuItem key={dept.id} value={dept.id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="status-label">Статус *</InputLabel>
              <Select
                labelId="status-label"
                label="Статус *"
                name="status_id"
                value={asset.status_id || ''}
                onChange={handleChange}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>

        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад
          </Button>
          <Box>
            <Button variant="contained" color="success" onClick={handleSubmit} sx={{ mr: 2 }}>
              Сохранить
            </Button>
            <Button variant="contained" color="error" onClick={handleDelete}>
              Удалить
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
