// frontend/src/components/AddAsset.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Alert,
  CircularProgress
} from '@mui/material';
import axios from 'axios';
import { API_BASE_URL } from '../config'; // Импорт базового URL

const AddAsset = ({ currentUser }) => {
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type_id: '', // Изменено с category_id
    brand: '',
    model: '',
    status_id: '',
    actual_user: '',
    responsible_person: '',
    department_id: '', // Стало обязательным
    room: '',
    purchase_date: '', // Стало обязательным
    comments: ''
  });

  // Состояния для выпадающих списков
  const [types, setTypes] = useState([]); // Изменено с categories
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);

  // Состояния для динамических полей
  const [typeProperties, setTypeProperties] = useState([]); // Свойства для выбранного типа
  const [dynamicFields, setDynamicFields] = useState({}); // Значения динамических полей

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  // Загрузка справочников
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [typesRes, statusesRes, employeesRes, departmentsRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }), // Изменено с /api/categories
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
        ]);

        setTypes(typesRes.data); // Изменено с setCategories
        setStatuses(statusesRes.data);
        setEmployees(employeesRes.data);
        setDepartments(departmentsRes.data);
      } catch (error) {
        console.error('Ошибка при загрузке справочников:', error);
        alert('Не удалось загрузить данные справочников.');
      }
    };

    fetchData();
  }, []);

  // Загрузка свойств типа при изменении type_id
  useEffect(() => {
    const loadTypeProperties = async () => {
      if (asset.type_id) {
        try {
          // Предполагается, что этот эндпоинт существует и возвращает свойства для типа
          const res = await axios.get(`${API_BASE_URL}/api/types/${asset.type_id}/properties`, { withCredentials: true });
          setTypeProperties(res.data);

          // Инициализируем значения динамических полей
          const initialDynamicFields = {};
          res.data.forEach(prop => {
            // Используем имя свойства как ключ для поля ввода
            // Нужно сопоставить имя свойства с полем в модели Asset
            const fieldName = mapPropertyNameToAssetField(prop.name);
            if (fieldName) {
              initialDynamicFields[fieldName] = ''; // Инициализируем пустой строкой
            }
          });
          setDynamicFields(initialDynamicFields);
        } catch (error) {
          console.error('Ошибка при загрузке свойств типа:', error);
          setTypeProperties([]);
          setDynamicFields({});
        }
      } else {
        setTypeProperties([]);
        setDynamicFields({});
      }
    };

    loadTypeProperties();
  }, [asset.type_id]);

  // Функция сопоставления имени свойства с полем в Asset
  const mapPropertyNameToAssetField = (propertyName) => {
    // Это сопоставление нужно адаптировать под реальные имена свойств в вашей БД
    const mapping = {
      'Диагональ': 'diagonal',
      'Диагональ (дюймы)': 'diagonal',
      // <<<--- ИЗМЕНЕНО: Переименовано "ОС" в "Операционная система"
      'Операционная система': 'OS',
      'Процессор': 'CPU',
      'Оперативная память (ГБ)': 'RAM',
      'Оперативная память': 'RAM',
      'Диск (HDD/SSD)': 'Drive',
      'Диск': 'Drive',
      'IP-адрес': 'IP_address', // В JS удобнее работать с IP_address, но отправим как IP-address
      'Внутренний номер': 'number',
      // Добавьте другие сопоставления, если они отличаются
    };
    return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_'); // fallback
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset((prev) => ({ ...prev, [name]: value }));

    // Очистка ошибок при изменении поля
    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  // <<<--- ИЗМЕНЕНО: НОВАЯ ФУНКЦИЯ: Обработчик изменения Ответственного
  const handleResponsiblePersonChange = (e) => {
    const employeeId = e.target.value;
    handleChange(e); // Обновляем поле responsible_person в состоянии asset

    if (employeeId) {
      // Найти выбранного сотрудника в уже загруженном списке
      const selectedEmployee = employees.find(emp => emp.id === parseInt(employeeId, 10));
      if (selectedEmployee && selectedEmployee.department_id) {
        // Автоматически устанавливаем department_id
        setAsset(prevAsset => ({
          ...prevAsset,
          department_id: selectedEmployee.department_id.toString() // Убедиться, что это строка
        }));

        // Очищаем ошибку для department_id, если она была
        if (errors.department_id) {
          setErrors(prevErrors => ({ ...prevErrors, department_id: '' }));
        }

      } else {
        // Если сотрудник не найден или у него нет отдела, очищаем отдел
        setAsset(prevAsset => ({
          ...prevAsset,
          department_id: ''
        }));
      }
    } else {
      // Если сотрудник не выбран, очищаем отдел
      setAsset(prevAsset => ({
        ...prevAsset,
        department_id: ''
      }));
    }
  };

  // Обработчик изменения динамических полей
  const handleDynamicFieldChange = (fieldName) => (e) => {
    const { value } = e.target;
    setDynamicFields((prev) => ({ ...prev, [fieldName]: value }));
    // Очистка ошибок для динамических полей, если они будут
    // if (errors[fieldName]) {
    //   setErrors((prev) => ({ ...prev, [fieldName]: '' }));
    // }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    // Проверка обязательных полей
    if (!asset.inventory_number) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.type_id) newErrors.type_id = 'Обязательное поле'; // Изменено с category_id
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!asset.responsible_person) newErrors.responsible_person = 'Обязательное поле';
    // <<<--- ИЗМЕНЕНО: Сделаны обязательными
    if (!asset.department_id) newErrors.department_id = 'Обязательное поле';
    if (!asset.purchase_date) newErrors.purchase_date = 'Обязательное поле';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      // Формируем данные для отправки, объединяя основные поля и динамические
      let dataToSend = {
        ...asset,
        ...dynamicFields // Добавляем значения динамических полей
        // Убедитесь, что имена полей в dynamicFields совпадают с ожидаемыми бэкендом (diagonal, CPU и т.д.)
      };

      // <<<--- ИЗМЕНЕНО: Обработка IP-адреса
      // Если ключ в dynamicFields - 'IP_address', меняем его на 'IP-address' для бэкенда
      // (Это зависит от того, как ваш бэкенд ожидает получить имя поля)
      if (dataToSend.hasOwnProperty('IP_address')) {
         dataToSend['IP-address'] = dataToSend.IP_address;
         delete dataToSend.IP_address;
      }

      await axios.post(`${API_BASE_URL}/api/assets`, dataToSend, { withCredentials: true });
      alert('Актив успешно добавлен');
      navigate('/assets');
    } catch (error) {
      console.error('Ошибка при добавлении актива:', error);
      // Попробуем получить сообщение об ошибке от сервера
      let errorMessage = 'Неизвестная ошибка';
      if (error.response) {
        // Сервер ответил ошибкой
        if (error.response.data && error.response.data.error) {
          errorMessage = error.response.data.error;
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        } else {
          errorMessage = `Ошибка ${error.response.status}: ${error.response.statusText}`;
        }
      } else if (error.request) {
        // Запрос был сделан, но ответа не было
        errorMessage = 'Нет ответа от сервера';
      } else {
        // Что-то пошло не так при настройке запроса
        errorMessage = error.message;
      }
      alert('Ошибка при добавлении актива: ' + errorMessage);
    }
  };

  // Функция для рендеринга динамического поля по имени свойства
  const renderDynamicField = (propertyName) => {
    const fieldName = mapPropertyNameToAssetField(propertyName);
    const value = dynamicFields[fieldName] || '';

    // Определяем тип поля ввода на основе имени свойства (можно усложнить)
    let fieldType = 'text';
    if (propertyName.toLowerCase().includes('дата')) {
      fieldType = 'date';
    }

    // Можно добавить больше логики для выбора типа (например, Select для статусов ОС)

    return (
      <TextField
        key={fieldName}
        label={propertyName}
        name={fieldName}
        value={value}
        onChange={handleDynamicFieldChange(fieldName)}
        fullWidth
        margin="normal"
        // Добавить error и helperText, если будут ошибки валидации для этих полей
        // error={!!errors[fieldName]}
        // helperText={errors[fieldName]}
      />
    );
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <Typography variant="h5">Добавить актив</Typography>

        <Grid container spacing={2}>
          {/* Серийный номер */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Инвентарный номер */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
            />
          </Grid>

          {/* Тип устройства (ранее Категория) */}
          <Grid item xs={12}>
            <FormControl fullWidth margin="normal" error={!!errors.type_id}>
              <InputLabel id="type-label">Тип устройства *</InputLabel> {/* Переименовано */}
              <Select
                labelId="type-label" // Переименовано
                label="Тип устройства *" // Переименовано
                name="type_id" // Изменено с category_id
                value={asset.type_id} // Изменено с category_id
                onChange={handleChange}
              >
                {types.map((type) => ( // Изменено с categories
                  <MenuItem key={type.id} value={type.id}>
                    {type.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.type_id && <FormHelperText>{errors.type_id}</FormHelperText>}
            </FormControl>
          </Grid>

          {/* Фирма */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Фирма"
              name="brand"
              value={asset.brand}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Модель */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Модель"
              name="model"
              value={asset.model}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Динамические поля, полученные из type_properties */}
          {typeProperties.map((property) => (
            <Grid item xs={12} key={property.id}>
              {renderDynamicField(property.name)}
            </Grid>
          ))}

          {/* Статус */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!errors.status_id}>
              <InputLabel id="status-label">Статус *</InputLabel>
              <Select
                labelId="status-label"
                label="Статус *"
                name="status_id"
                value={asset.status_id}
                onChange={handleChange}
              >
                {statuses.map((status) => (
                  <MenuItem key={status.id} value={status.id}>
                    {status.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.status_id && <FormHelperText>{errors.status_id}</FormHelperText>}
            </FormControl>
          </Grid>

           {/* Фактический пользователь */}
           <Grid item xs={12} sm={6}>
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Ответственный */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!errors.responsible_person}>
              <InputLabel id="responsible-person-label">Ответственный *</InputLabel>
              <Select
                labelId="responsible-person-label"
                label="Ответственный *"
                name="responsible_person"
                value={asset.responsible_person}
                // <<<--- ИЗМЕНЕНО: Используем новую функцию
                onChange={handleResponsiblePersonChange}
              >
                {employees.map((emp) => (
                  <MenuItem key={emp.id} value={emp.id}>
                    {emp.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.responsible_person && <FormHelperText>{errors.responsible_person}</FormHelperText>}
            </FormControl>
          </Grid>

          {/* Отдел (подразделение) */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth margin="normal" error={!!errors.department_id}>
              <InputLabel id="department-label">Отдел (подразделение) *</InputLabel>
              <Select
                labelId="department-label"
                label="Отдел (подразделение) *"
                name="department_id"
                value={asset.department_id}
                onChange={handleChange}
                // <<<--- ИЗМЕНЕНО: Поле больше не заблокировано
                // disabled
                // style={{backgroundColor: '#f5f5f5',color: 'rgba(0, 0, 0, 0.38)'}}
              >
                {departments.map((dept) => (
                  <MenuItem key={dept.id} value={dept.id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.department_id && <FormHelperText>{errors.department_id}</FormHelperText>}
            </FormControl>
          </Grid>

          {/* Помещение */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Помещение"
              name="room"
              value={asset.room}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Дата покупки */}
          <Grid item xs={12} sm={6}>
            <TextField
              label="Дата покупки *"
              name="purchase_date"
              type="date"
              value={asset.purchase_date}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.purchase_date}
              helperText={errors.purchase_date}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Комментарий */}
          <Grid item xs={12}>
            <TextField
              label="Комментарий"
              name="comments"
              value={asset.comments}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
          </Grid>
        </Grid>

        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          style={{ marginTop: '20px' }}
          disabled={!currentUser || currentUser.role !== 'admin'} // Блокировка для не-админов
        >
          Добавить актив
        </Button>
      </Box>
    </Container>
  );
};

export default AddAsset;
