import React, { useState } from 'react';
import axios from 'axios';
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  Alert
} from '@mui/material';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const res = await axios.post(
        'http://localhost:5000/api/login',
        { username, password },
        { withCredentials: true } // <-- ВАЖНО для работы сессии
      );

      if (res.status === 200 && res.data.role === 'admin') {
        onLogin({ username: res.data.username, role: res.data.role });
      } else {
        setError('Только администратор может войти');
      }
    } catch (err) {
      console.error(err);
      setError('Ошибка при входе: неверный логин или пароль');
    }
  };

  return (
    <Container maxWidth="xs" style={{ marginTop: '50px' }}>
      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <Typography variant="h5">Вход</Typography>

        {error && <Alert severity="error">{error}</Alert>}

        <TextField
          label="Логин"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
          fullWidth
        />

        <TextField
          label="Пароль"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          fullWidth
        />

        <Button variant="contained" color="primary" type="submit" fullWidth>
          Войти
        </Button>
      </Box>
    </Container>
  );
};

export default Login;
