// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Box,
  TextField,
  Button,
  Snackbar,
  Alert,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const [asset, setAsset] = useState(null);
  const [isEditing, setIsEditing] = useState(false); // Состояние для режима редактирования
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        setSnackbar({
          open: true,
          message: 'Не удалось загрузить данные актива',
          severity: 'error'
        });
      });
  }, [id]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      setIsEditing(false); // Выйти из режима редактирования
      setSnackbar({
        open: true,
        message: 'Данные успешно сохранены',
        severity: 'success'
      });
    } catch (err) {
      console.error('Ошибка сохранения:', err);
      setSnackbar({
        open: true,
        message: 'Не удалось сохранить изменения',
        severity: 'error'
      });
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', backgroundColor: '#fff' }}>
        <Typography variant="h5" gutterBottom>{asset?.name}</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset?.serial_number || ''}
              onChange={(e) => setAsset({ ...asset, serial_number: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset?.inventory_number || ''}
              onChange={(e) => setAsset({ ...asset, inventory_number: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset?.name || ''}
              onChange={(e) => setAsset({ ...asset, name: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset?.location || ''}
              onChange={(e) => setAsset({ ...asset, location: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset?.responsible_person || ''}
              onChange={(e) => setAsset({ ...asset, responsible_person: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset?.actual_user || ''}
              onChange={(e) => setAsset({ ...asset, actual_user: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset?.comments || ''}
              onChange={(e) => setAsset({ ...asset, comments: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset?.status_id || ''}
              onChange={(e) => setAsset({ ...asset, status_id: e.target.value })}
              disabled={!currentUser || currentUser.role !== 'admin' || !isEditing}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset?.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>

          {currentUser && currentUser.role === 'admin' && (
            <Button variant="contained" color="primary" onClick={handleSave}>
              Сохранить изменения
            </Button>
          )}

          <Button variant="contained" color="info" onClick={handlePrintQR}>
            Печать QR-кода
          </Button>
        </Box>
      </Paper>

      {/* Snackbar для уведомлений */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default AssetDetails;
