// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type: '',         // Новое поле
    brand: '',        // Обязательное
    model: '',        // Обязательное
    category_id: '',
    department_id: '',     // Отдел
    room: '',              // Помещение
    purchase_date: '',     // Дата покупки
    responsible_person: '', // Теперь это ID сотрудника
    actual_user: '',
    comments: '',
    status_id: ''
  });
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [errors, setErrors] = useState({});

  // Загружаем актив по ID при монтировании
  useEffect(() => {
    if (!id || id === 'new') return;

    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => {
        setAsset(res.data);
      })
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        alert('Не удалось загрузить актив');
        navigate('/assets');
      });

    // Загружаем справочные данные
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data))
      .catch(err => console.error('Ошибка загрузки категорий:', err));

    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data))
      .catch(err => console.error('Ошибка загрузки статусов:', err));

    axios.get('http://localhost:5000/api/departments')
      .then(res => setDepartments(res.data))
      .catch(err => console.error('Ошибка загрузки отделов:', err));

    axios.get('http://localhost:5000/api/employees')
      .then(res => setEmployees(res.data))
      .catch(err => console.error('Ошибка загрузки сотрудников:', err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset({ ...asset, [name]: value });
  };

  const handleResponsiblePersonChange = async (e) => {
    const selectedEmployeeId = e.target.value;

    try {
      // Получаем отдел сотрудника
      const res = await axios.get(`http://localhost:5000/api/employees/${selectedEmployeeId}`);
      const departmentId = res.data.department_id;

      // Обновляем поля "Ответственный" и "Отдел"
      setAsset({
        ...asset,
        responsible_person: selectedEmployeeId,
        department_id: departmentId,
      });
    } catch (err) {
      console.error('Ошибка при получении отдела сотрудника:', err);
      alert('Не удалось получить отдел сотрудника');
    }
  };
  
  
  
  const handleSave = async () => {
    // Проверка обязательных полей
    const newErrors = {};
    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.category_id) newErrors.category_id = 'Обязательное поле';
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!asset.responsible_person) newErrors.responsible_person = 'Обязательное поле'; // ← Новое правило
    if (!asset.purchase_date) newErrors.purchase_date = 'Обязательное поле';


    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно обновлены');
    } catch (err) {
      alert('Ошибка при сохранении изменений');
      console.error(err);
    }
  };

  const handlePrintQRCode = async () => {
    if (!asset.inventory_number) {
      alert('Инвентарный номер не указан');
      return;
    }

    try {
      // Получаем QR-код с сервера
      const res = await axios.post('http://localhost:5000/api/qrcodes', {
        ids: [asset.id],
      });
      const codes = res.data;

      // Генерируем HTML-контент для печати
      let htmlContent = `
        <html>
          <head>
            <style>
              @page {
                size: A4;
                margin: 0;
                @top-center { content: ''; }
                @bottom-left { content: ''; }
                @bottom-right { content: ''; }
              }
              body {
                display: flex;
                flex-wrap: wrap;
                gap: 10px;
                padding: 20px;
                background-color: #fff;
              }
              .qr-wrapper {
                width: 3cm; /* Ширина контейнера */
                height: 3cm; /* Высота контейнера */
                position: relative;
                font-size: 10px;
                text-align: center;
              }
              .qr-container {
                width: 100%;
                height: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
              }
              img {
                max-width: 100%;
                max-height: 100%;
                object-fit: contain;
              }
              .text-top, .text-bottom {
                position: absolute;
                left: 50%; /* Центрирование по горизонтали */
                transform: translateX(-50%); /* Корректировка центрирования */
                white-space: nowrap; /* Запрет переноса строки */
                overflow: hidden; /* Скрывает лишний текст */
                text-overflow: ellipsis; /* Добавляет многоточие при обрезке */
                width: 90%; /* Ограничиваем ширину текста */
                font-size: 8px; /* Маленький размер шрифта */
              }
              .text-top {
                top: 0;
              }
              .text-bottom {
                bottom: 0;
              }
            </style>
          </head>
          <body>
      `;

      codes.forEach((code) => {
        htmlContent += `
          <div class="qr-wrapper">
            <div class="qr-container">
              <img src="data:image/png;base64,${code.qr_base64}" />
            </div>
            <span class="text-top">${code.name}</span>
            <span class="text-bottom">${code.inventory_number}</span>
          </div>
        `;
      });

      htmlContent += '</body></html>';

      // Создаем новое окно для печати
      const printWindow = window.open('', '_blank');
      printWindow.document.write(htmlContent);

      setTimeout(() => {
        printWindow.print();
        printWindow.close();
      }, 500);
    } catch (err) {
      console.error('Ошибка при генерации QR-кода:', err);
      alert('Не удалось получить QR-код с сервера');
    }
  };

  if (!asset) {
    return (
      <Container maxWidth="md">
        <Paper elevation={3} style={{ padding: '20px' }}>
          <Typography variant="h5">Актив не найден</Typography>
          <Button variant="outlined" onClick={() => navigate('/assets')}>
            Назад
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        {/* Шапка с полным названием */}
        <Typography variant="h5" gutterBottom>
          {asset.type} {asset.brand} {asset.model}
        </Typography>

        <Grid container spacing={2}>
          {/* Левая колонка */}
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            <FormControl fullWidth margin="normal" error={!!errors.category_id} disabled={!currentUser || currentUser.role !== 'admin'}>
              <InputLabel id="category-label">Категория *</InputLabel>
              <Select
                labelId="category-label"
                label="Категория *"
                name="category_id"
                value={asset.category_id || ''}
                onChange={handleChange}
              >
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.category_id && (
                <Typography color="error" variant="caption">
                  {errors.category_id}
                </Typography>
              )}
            </FormControl>

            <TextField
              label="Фирма"
              name="brand"
              value={asset.brand || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            <TextField
              label="Модель"
              name="model"
              value={asset.model || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            {/* Поле "Дата покупки" */}
            <TextField
              label="Дата покупки *"
              name="purchase_date"
              type="date"
              value={asset.purchase_date || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.purchase_date}
              helperText={errors.purchase_date}              
              InputLabelProps={{
                shrink: true,
              }}
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>

          {/* Правая колонка */}
          <Grid item xs={6}>

            <TextField
              label="Конфигурация (Процессор/DDR/HDD)"
              name="type"
              value={asset.type || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            <FormControl fullWidth margin="normal" error={!!errors.status_id} disabled={!currentUser || currentUser.role !== 'admin'}>
              <InputLabel id="status-label">Статус *</InputLabel>
              <Select
                labelId="status-label"
                label="Статус *"
                name="status_id"
                value={asset.status_id || ''}
                onChange={handleChange}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.status_id && (
                <Typography color="error" variant="caption">
                  {errors.status_id}
                </Typography>
              )}
            </FormControl>

              {/* Фактический пользователь */}
              <TextField
                label="Фактический пользователь"
                name="actual_user"
                value={asset.actual_user || ''}
                onChange={handleChange}
                fullWidth
                margin="normal"
                disabled={!currentUser || currentUser.role !== 'admin'}
              />

            {/* Выпадающий список: Ответственный */}
            <FormControl fullWidth margin="normal" error={!!errors.responsible_person} disabled={!currentUser || currentUser.role !== 'admin'}>
              <InputLabel id="employee-label">Ответственный *</InputLabel>
              <Select
                labelId="employee-label"
                label="Ответственный *"
                name="responsible_person"
                value={asset.responsible_person || ''}
                onChange={handleResponsiblePersonChange}
              >
                {employees.map(emp => (
                  <MenuItem key={emp.id} value={emp.id}>
                    {emp.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.responsible_person && (
                <Typography color="error" variant="caption">
                  {errors.responsible_person}
                </Typography>
              )}
            </FormControl>


            {/* Выпадающий список: Отдел */}
            <FormControl fullWidth margin="normal" disabled={!currentUser || currentUser.role !== 'admin'}>
              <InputLabel id="department-label">
              Отдел
              </InputLabel>
              <Select
                labelId="department-label"
                label="Отдел"
                name="department_id"
                value={asset.department_id || ''}
                onChange={handleChange}
              >
                {departments.map(dept => (
                  <MenuItem key={dept.id} value={dept.id}>
                    {dept.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {/* Текстовое поле: Помещение */}
            <TextField
              label="Помещение"
              name="room"
              value={asset.room || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            {/* Комментарии */}
            <TextField
              label="Комментарий"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={`http://localhost:3000/assets/${asset.id}`}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопка печати QR-кода */}
        <Box mt={0.5} display="flex" justifyContent="center">
          <Button variant="contained" color="primary" onClick={handlePrintQRCode}>
            Печать QR
          </Button>
        </Box>

        {/* Кнопки управления */}
        <Box mt={4.5} display="flex" justifyContent="space-between" alignItems="center">
          <Button variant="outlined" onClick={() => navigate('/assets')}>
            Назад
          </Button>
          <Box>
            {currentUser && currentUser.role === 'admin' && (
              <>
                <Button
                  variant="contained"
                  color="error"
                  onClick={() => {
                    if (!window.confirm('Вы уверены, что хотите удалить этот актив?')) return;
                    try {
                      axios.delete(`http://localhost:5000/api/assets/${id}`);
                      alert('Актив успешно удален');
                      navigate('/assets');
                    } catch (err) {
                      alert('Ошибка при удалении актива');
                      console.error(err);
                    }
                  }}
                  sx={{ mr: '8px' }}
                >
                  Удалить
                </Button>
                <Button
                  variant="contained"
                  color="success"
                  onClick={handleSave}
                >
                  Сохранить
                </Button>
              </>
            )}
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
