import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Table,
  TableHead,
  TableRow,
  TableContainer,
  TableCell,
  TableBody,
  Button,
  Checkbox,
  Box,
} from '@mui/material';
import { useNavigate, useLocation } from 'react-router-dom';
import * as XLSX from 'xlsx';

const AssetList = ({currentUser}) => {
  const [asset, setAsset] = useState({
  serial_number: '',
  inventory_number: '',
  type: '',         // Новое поле
  brand: '',        // Обязательное
  model: '',        // Обязательное
  category_id: '',
  department_id: '',     // Отдел
  room: '',              // Помещение
  purchase_date: '',     // Дата покупки
  responsible_person: '', // Теперь это ID сотрудника
  actual_user: '',
  comments: '',
  status_id: ''
  });
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState('inventory_number');
  const [sortOrder, setSortOrder] = useState('asc');
  const [selectedAssets, setSelectedAssets] = useState([]);
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  // Загрузка данных
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const statsRes = await axios.get('http://localhost:5000/api/statuses');
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const catsRes = await axios.get('http://localhost:5000/api/categories');

        setAssets(assetsRes.data);
        setDepartments(depsRes.data);
        setStatuses(statsRes.data);
        setEmployees(empsRes.data);
        setCategories(catsRes.data);
        
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  // Запрет перехода со страницы при выделеных чекбоксах
  const navigateWithConfirm = (path) => {
  if (selectedAssets.length > 0) {
    const userConfirmed = window.confirm(
      'Снять выделение и перейти на другую страницу?'
    );
    if (!userConfirmed) return; // Переход заблокирован
    setSelectedAssets([]); // Снимаем выделение
  }
  navigate(path);
};

  // Форматирование даты в DD.MM.YYYY
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    if (isNaN(date)) return '';

    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();

    return `${day}.${month}.${year}`;
  };

  // Форматирование ФИО → Иванов И.И.
  const formatEmployeeName = (employeeId) => {
    if (!employeeId) return '';

    const employee = employees.find(emp => emp.id === employeeId);
    if (!employee) return '';

    const parts = employee.name.split(' ');
    const surname = parts[0] || '';
    const firstInitial = parts[1]?.charAt(0).toUpperCase() || '';
    const patronymicInitial = parts[2]?.charAt(0).toUpperCase() || '';

    return `${surname} ${firstInitial + '.'}${patronymicInitial + '.'}`;
  };

  
  // Получение имени статуса
  const getStatusName = (statusId) => {
    const status = statuses.find(stat => stat.id === statusId);
    return status?.name || '';
  };
  
  // Получение имени отдела
  const getDepartmentName = (departmentId) => {
    const department = departments.find(d => d.id === departmentId);
    return department?.name || '';
  };
  
  // Фильтрация по строке поиска
  const filteredAssets = assets.filter(asset => {
    const searchLower = searchTerm.toLowerCase();
    
    const assetFieldsToSearch = [
      asset.inventory_number,
      `${asset.category_name} ${asset.brand} ${asset.model}`,
      asset.type,
      getStatusName(asset.status_id),
      formatEmployeeName(asset.responsible_person),
      asset.actual_user,
      getDepartmentName(asset.department_id),
      asset.room || '',
      formatDate(asset.purchase_date),
    ].join(' ').toLowerCase();
    
    return assetFieldsToSearch.includes(searchLower);
  });
  
  // Сортировка активов
  const sortedAssets = [...filteredAssets].sort((a, b) => {
    let comparison = 0;
    
    switch (sortColumn) {
      case 'inventory_number':
        comparison = a.inventory_number.localeCompare(b.inventory_number, undefined, { numeric: true });
        break;
        case 'name':
          comparison = a.category_name.localeCompare(b.category_name, undefined, { sensitivity: 'base' });
          break;
          case 'brand':
            comparison = a.brand.localeCompare(b.brand, undefined, { sensitivity: 'base' });
            break;
            case 'model':
              comparison = a.model.localeCompare(b.model, undefined, { sensitivity: 'base' });
              break;
              case 'type':
                comparison = a.type.localeCompare(b.type, undefined, { sensitivity: 'base' });
                break;
                case 'status':
                  const statusA = getStatusName(a.status_id).toLowerCase();
                  const statusB = getStatusName(b.status_id).toLowerCase();
                  comparison = statusA.localeCompare(statusB);
                  break;
                  case 'responsible':
                    const empA = formatEmployeeName(a.responsible_person).toLowerCase();
                    const empB = formatEmployeeName(b.responsible_person).toLowerCase();
                    comparison = empA.localeCompare(empB);
                    break;
                    case 'actual_user':
                    comparison = a.actual_user.localeCompare(b.actual_user, undefined, { numeric: true });
                      break;                    
                      case 'department':
                        const deptA = getDepartmentName(a.department_id).toLowerCase();
                        const deptB = getDepartmentName(b.department_id).toLowerCase();
                        comparison = deptA.localeCompare(deptB);
                        break;
                        case 'room':
                          const roomA = (a.room || '').toLowerCase();
                          const roomB = (b.room || '').toLowerCase();
                          comparison = roomA.localeCompare(roomB);
                          break;
                          case 'purchase_date':
                            comparison = new Date(a.purchase_date) - new Date(b.purchase_date);
                            break;
                            default:
                              return 0;
                            }
                        
    if (sortOrder === 'desc') {
      comparison = -comparison;
    }
    
    return comparison;
  });
  
  // Обработчик клика на заголовок столбца
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };
  
  // Удаление выбранных активов
  const handleDeleteSelected = async () => {
    if (!window.confirm(`Вы уверены, что хотите удалить ${selectedAssets.length} активов?`)) return;
    
    try {
      await axios.delete('http://localhost:5000/api/assets', {
        data: { ids: selectedAssets },
      });
      
      // Убираем удаленные активы из списка
      setAssets(prev => prev.filter(asset => !selectedAssets.includes(asset.id)));
      setSelectedAssets([]);
      alert('Выбранные активы успешно удалены');
    } catch (err) {
      alert('Ошибка при удалении активов');
      console.error(err);
    }
  };
  
  // Печать QR-кодов
  const handlePrintQRCode = async () => {
    if (selectedAssets.length === 0) {
      alert('Выберите хотя бы один актив');
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/api/qrcodes', {
        ids: selectedAssets
      });
      const codes = res.data;

      let htmlContent = `
        <html>
          <head>
            <style>
              @page {
                size: A4;
                margin: 0;
              }

              body {
                width: 21cm;
                padding: 0;
                background-color: #fff;
                font-family: Arial, sans-serif;
              }

              .page {
                display: flex;
                flex-wrap: wrap;
                gap: 10px;
                padding: 20px;
                box-sizing: border-box;
                page-break-after: always;
                break-after: page;
              }

              .qr-wrapper {
                width: 3cm;
                height: 3cm;
                position: relative;
                text-align: center;
                font-size: 10px;
                box-sizing: border-box;
                page-break-inside: avoid;
                break-inside: avoid;
              }

              .qr-container {
                width: 100%;
                height: calc (100%);
                display: flex;
                justify-content: center;
                align-items: center;
              }

              img {
                max-width: 100%;
                max-height: 100%;
                object-fit: contain;
              }

              .text-top,
              .text-bottom {
                position: absolute;
                left: 50%;
                transform: translateX(-50%);
                white-space: nowrap;
                overflow: hidden;
                text-overflow: ellipsis;
                width: 90%;
                font-size: 8px;
              }

              .text-top {
                top: 0;
              }

              .text-bottom {
                bottom: 0;
              }
            </style>
          </head>
          <body>
      `;

      // Группируем по 48 кодов на страницу
      const itemsPerPage = 48;

      for (let i = 0; i < codes.length; i += itemsPerPage) {
        const pageItems = codes.slice(i, i + itemsPerPage);

        htmlContent += `<div class="page">`;

        pageItems.forEach((code) => {
          htmlContent += `
            <div class="qr-wrapper">
              <div class="qr-container">
                <img src="data:image/png;base64,${code.qr_base64}" />
              </div>
              <span class="text-top">${code.name}</span>
              <span class="text-bottom">${code.inventory_number}</span>
            </div>
          `;
        });

        htmlContent += `</div>`;
      }

      htmlContent += '</body></html>';

      const printWindow = window.open('', '_blank');
      printWindow.document.write(htmlContent);

      setTimeout(() => {
        printWindow.print();
        printWindow.close();
      }, 500);
    } catch (err) {
      console.error('Ошибка при генерации QR-кодов:', err);
      alert('Не удалось получить QR-коды с сервера');
    }
  };
  
  // Экспорт Excel
  const handleExportExcel = () => {
    // Фильтруем активы: только выделенные, или все, если ничего не выбрано
    const filteredAssets = selectedAssets.length > 0
      ? assets.filter(asset => selectedAssets.includes(asset.id))
      : assets;

    // Преобразование данных для Excel
    const dataToExport = filteredAssets.map((asset, index) => {
      const category = categories.find((cat) => cat.id === asset.category_id)?.name || '';
      const status = statuses.find((stat) => stat.id === asset.status_id)?.name || '';
      const department = departments.find((dept) => dept.id === asset.department_id)?.name || '';
      const responsiblePerson = formatEmployeeName(asset.responsible_person);

      return [
        index + 1,
        asset.serial_number || '',
        asset.inventory_number || '',
        category + asset.brand + asset.model || '',
        asset.brand || '',
        asset.model || '',
        asset.type || '',
        status || '',
        responsiblePerson || '',
        asset.actual_user || '',
        department || '',
        asset.room || '',
        formatDate(asset.purchase_date) || '',
        asset.comments || ''
      ];
    });

    // Заголовки таблицы
    const headers = [
      '№',
      'Серийный номер',
      'Инвентарный номер',
      'Категория',
      'Фирма',
      'Модель',
      'Конфигурация',
      'Статус',
      'Ответственный',
      'Фактический пользователь',
      'Отдел (подразделение)',
      'Помещение',
      'Дата покупки',
      'Комментарий'
    ];

    // Добавление заголовков в начало
    const worksheetData = [headers, ...dataToExport];

    // Создание листа Excel
    const ws = XLSX.utils.aoa_to_sheet(worksheetData);

    // Автоподбор ширины столбцов
    const colWidths = worksheetData[0].map((_, colIndex) => {
      const maxLength = Math.max(
        ...worksheetData.map(row => row[colIndex] ? row[colIndex].toString().length : 0)
      );
      return { wch: Math.min(maxLength + 2, 50) }; // Ограничиваем ширину до 50 символов
    });
    ws['!cols'] = colWidths;

    // Создаём книгу и сохраняем файл
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, ws, 'Оборудования');
    XLSX.writeFile(workbook, 'Оборудования.xlsx');
  };

  return (
    <Container maxWidth="">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>Список оборудования</strong>
        </Typography>

        {/* Поисковая строка */}
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* Кнопки управления */}
        <Box display="flex" alignItems="center" mb={2}>
          <Button
            variant="contained"
            color="primary"
            disabled={selectedAssets.length === 0}
            onClick={handleExportExcel}
            style={{ marginRight: '8px' }}
          >
            Экспорт Excel
          </Button>
          <Button
            variant="contained"
            color="primary"
            disabled={selectedAssets.length === 0}
            onClick={handlePrintQRCode}
            style={{ marginRight: '8px' }}
          >
            Печать QR
          </Button>


          {currentUser && currentUser.role === 'admin' && (
            <>
              <Button
                variant="contained"
                color="error"
                disabled={selectedAssets.length === 0}
                onClick={handleDeleteSelected}
                style={{ marginRight: '8px' }}
              >
                Удалить
              </Button>
              <Button
                variant="contained"
                color="success"
                onClick={() => navigateWithConfirm('/assets/new')}
                style={{ marginRight: '8px' }}
              >
                Добавить
              </Button>
            </>
          )}
        </Box>

        {/* Таблица активов */}
        <TableContainer component={Paper}>
          <Table size="small">
            <TableHead>
              <TableRow>
                <TableCell padding="checkbox">
                  <Checkbox
                    indeterminate={
                      selectedAssets.length > 0 &&
                      selectedAssets.length < filteredAssets.length
                    }
                    checked={selectedAssets.length === filteredAssets.length}
                    onChange={(e) => {
                      if (e.target.checked) {
                        const allIds = filteredAssets.map((asset) => asset.id);
                        setSelectedAssets(allIds);
                      } else {
                        setSelectedAssets([]);
                      }
                    }}
                  />
                </TableCell>
                <TableCell
                  onClick={() => handleSort('inventory_number')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Инвентарный номер{' '}
                  {sortColumn === 'inventory_number' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('name')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Наименование{' '}
                  {sortColumn === 'name' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('type')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Конфигурация{' '}
                  {sortColumn === 'type' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('status')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Статус{' '}
                  {sortColumn === 'status' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('responsible')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Ответственный{' '}
                  {sortColumn === 'responsible' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('actual_user')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Пользователь{' '}
                  {sortColumn === 'actual_user' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>                
                <TableCell
                  onClick={() => handleSort('department')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Отдел (подразделение){' '}
                  {sortColumn === 'department' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('room')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Помещение{' '}
                  {sortColumn === 'room' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
                <TableCell
                  onClick={() => handleSort('purchase_date')}
                  sx={{ cursor: 'pointer', fontWeight: 'bold' }}
                >
                  Дата покупки{' '}
                  {sortColumn === 'purchase_date' && <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>}
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sortedAssets.map((asset) => (
                <TableRow
                  key={asset.id}
                  hover
                  onClick={() => navigateWithConfirm(`/assets/${asset.id}`)}
                  style={{ cursor: 'pointer' }}
                >
                <TableCell padding="checkbox">
                  <Checkbox
                    checked={selectedAssets.includes(asset.id)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setSelectedAssets([...selectedAssets, asset.id]);
                      } else {
                        setSelectedAssets(selectedAssets.filter(id => id !== asset.id));
                      }
                    }}
                    onClick={(e) => e.stopPropagation()} // Чтобы клик по чекбоксу не открывал карточку актива
                  />
                </TableCell>
                  <TableCell>{asset.inventory_number}</TableCell>
                  <TableCell>{`${asset.category_name || ''} ${asset.brand || ''} ${asset.model || ''}`.trim() || ''}</TableCell>
                  <TableCell>{asset.type}</TableCell>
                  <TableCell>{getStatusName(asset.status_id)}</TableCell>
                  <TableCell>{formatEmployeeName(asset.responsible_person)}</TableCell>
                  <TableCell>{asset.actual_user}</TableCell>
                  <TableCell>{getDepartmentName(asset.department_id)}</TableCell>
                  <TableCell>{asset.room || ''}</TableCell>
                  <TableCell>{asset.purchase_date ? formatDate(asset.purchase_date) : ''}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default AssetList;
