import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const depsRes = await axios.get('http://localhost:5000/api/departments');

        setEmployees(empsRes.data);
        setDepartments(depsRes.data);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  // Группируем сотрудников по отделам
  const employeesByDepartment = {};
  employees.forEach(emp => {
    if (!employeesByDepartment[emp.department_id]) {
      employeesByDepartment[emp.department_id] = [];
    }
    employeesByDepartment[emp.department_id].push(emp);
  });

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        {/* Отображаем отделы и их сотрудников */}
        {departments.map(dept => {
          const deptEmployees = employeesByDepartment[dept.id] || [];
          return (
            <div key={dept.id}>
              <Typography variant="h6" gutterBottom>
                {dept.name}
              </Typography>
              <Table size="small">
                <TableBody>
                  {deptEmployees.map(emp => {
                    const fullName = emp.name.split(' ');
                    const surname = fullName[0] || '';
                    const name = fullName[1] || '';
                    const patronymic = fullName[2] || '';

                    return (
                      <TableRow key={emp.id}>
                        <TableCell>{surname}</TableCell>
                        <TableCell>{name}</TableCell>
                        <TableCell>{patronymic}</TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </div>
          );
        })}
      </Paper>
    </Container>
  );
};

export default EmployeeList;
