// frontend/src/components/EmployeeList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/employees');
        setEmployees(res.data);
      } catch (err) {
        console.error('Ошибка при загрузке сотрудников:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Фамилия</TableCell>
              <TableCell>Имя</TableCell>
              <TableCell>Отчество</TableCell>
              <TableCell>Должность</TableCell>
              <TableCell>Отдел</TableCell>
              <TableCell>Помещение</TableCell>
              <TableCell>Вн. телефон</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {employees.map(emp => {
              return (
                <TableRow key={emp.id}>
                  <TableCell>{emp.surname}</TableCell>
                  <TableCell>{emp.name}</TableCell>
                  <TableCell>{emp.patronymic || '-'}</TableCell>
                  <TableCell>{emp.position || '-'}</TableCell>
                  <TableCell>{emp.department?.name || 'Не указано'}</TableCell>
                  <TableCell>{emp.room || '-'}</TableCell>
                  <TableCell>{emp.internal_phone || '-'}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default EmployeeList;
