// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AssetList from './components/AssetList';
import AssetDetails from './components/AssetDetails';
import Sidebar from './components/Sidebar';
import Header from './components/Header'; // Новый компонент
import AddAsset from './components/AddAsset';
import ProtectedRoute from './components/ProtectedRoute'; // Импортируем ProtectedRoute

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null); // Очищаем состояние
    window.location.href = '/login'; // Перенаправляем на страницу входа
  };

  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Шапка с именем пользователя */}
        {currentUser && <Header username={currentUser.username} role={currentUser.role} onLogout={handleLogout} />}

        <div style={{ display: 'flex', flexGrow: 1 }}>
          {/* Меню слева */}
          {currentUser && <Sidebar role={currentUser.role} />}

          {/* Основной контент */}
          <div style={{
            flex: 1,
            backgroundColor: '#f9f9f9',
            padding: '20px'
          }}>
            <Routes>
              {/* Страница входа */}
              <Route path="/login" element={
                !currentUser ? (
                  <Login onLogin={handleLogin} />
                ) : (
                  <Navigate to="/" replace />
                )
              } />

              {/* Защищённые маршруты */}
              <Route element={<ProtectedRoute currentUser={currentUser} />}>
                <Route path="/" element={<AssetList currentUser={currentUser} />} />
                <Route path="/assets/:id" element={<AssetDetails currentUser={currentUser} />} />
                <Route path="/assets/new" element={<AddAsset />} />
              </Route>
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
