// frontend/src/components/AddAsset.js
import React, { useState, useEffect } from 'react';
import {
  Container,
  Box,
  TextField,
  Button,
  Typography,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormHelperText,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle
} from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { API_BASE_URL } from '../config';

const AddAsset = ({ currentUser }) => {
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type_id: '',
    brand: '',
    model: '',
    status_id: '',
    actual_user: '',
    responsible_person: '',
    department_id: '',
    room: '',
    purchase_date: '',
    comments: ''
  });

  const [types, setTypes] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);

  const [typeProperties, setTypeProperties] = useState([]);
  const [dynamicFields, setDynamicFields] = useState({});

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const [openCancelDialog, setOpenCancelDialog] = useState(false);

  // Функция для сортировки массивов по имени
  const sortByLabel = (a, b) => {
    return (a.name || '').localeCompare(b.name || '', 'ru', { sensitivity: 'base' });
  };

  // Загрузка справочников
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [typesRes, statusesRes, employeesRes, departmentsRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/types`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/statuses`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/employees`, { withCredentials: true }),
          axios.get(`${API_BASE_URL}/api/departments`, { withCredentials: true }),
        ]);

        setTypes(typesRes.data.sort(sortByLabel));
        setStatuses(statusesRes.data.sort(sortByLabel));
        setEmployees(employeesRes.data.sort(sortByLabel));
        setDepartments(departmentsRes.data.sort(sortByLabel));
      } catch (error) {
        console.error('Ошибка при загрузке справочников:', error);
        alert('Не удалось загрузить данные справочников.');
      }
    };

    fetchData();
  }, []);

  // Загрузка свойств типа при изменении type_id
  useEffect(() => {
    const loadTypeProperties = async () => {
      if (asset.type_id) {
        try {
          const res = await axios.get(`${API_BASE_URL}/api/types/${asset.type_id}/properties`, { withCredentials: true });
          setTypeProperties(res.data);

          const initialDynamicFields = {};
          res.data.forEach(prop => {
            const fieldName = mapPropertyNameToAssetField(prop.name);
            if (fieldName) {
              initialDynamicFields[fieldName] = '';
            }
          });
          setDynamicFields(initialDynamicFields);
        } catch (error) {
          console.error('Ошибка при загрузке свойств типа:', error);
          setTypeProperties([]);
          setDynamicFields({});
        }
      } else {
        setTypeProperties([]);
        setDynamicFields({});
      }
    };

    loadTypeProperties();
  }, [asset.type_id]);

  const mapPropertyNameToAssetField = (propertyName) => {
    const mapping = {
      'Диагональ': 'diagonal',
      'Диагональ (дюймы)': 'diagonal',
      'Операционная система': 'OS',
      'Процессор': 'CPU',
      'Оперативная память (ГБ)': 'RAM',
      'Оперативная память': 'RAM',
      'Диск (HDD/SSD)': 'Drive',
      'Диск': 'Drive',
      'IP-адрес': 'IP_address',
      'Внутренний номер': 'number',
    };
    return mapping[propertyName] || propertyName.toLowerCase().replace(/\s+/g, '_');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset((prev) => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  // <<<--- ИЗМЕНЕНО: Восстановлена логика автоматического заполнения отдела
  const handleResponsiblePersonChange = (e) => {
    const employeeId = e.target.value;
    handleChange(e); // Обновляем поле responsible_person

    if (employeeId) {
      // Найти выбранного сотрудника в уже загруженном списке
      const selectedEmployee = employees.find(emp => emp.id === parseInt(employeeId, 10));
      if (selectedEmployee && selectedEmployee.department_id) {
        // Автоматически устанавливаем department_id из данных сотрудника
        setAsset(prevAsset => ({
          ...prevAsset,
          department_id: selectedEmployee.department_id.toString() // Убедиться, что это строка
        }));

        // Очищаем ошибку для department_id, если она была
        if (errors.department_id) {
          setErrors(prevErrors => ({ ...prevErrors, department_id: '' }));
        }
      } else {
        // Если у сотрудника нет отдела или сотрудник не найден, очищаем поле отдела
        setAsset(prevAsset => ({
          ...prevAsset,
          department_id: ''
        }));
      }
    } else {
      // Если сотрудник не выбран, очищаем отдел
      setAsset(prevAsset => ({
        ...prevAsset,
        department_id: ''
      }));
    }
  };

  const handleDynamicFieldChange = (fieldName) => (e) => {
    const { value } = e.target;
    setDynamicFields((prev) => ({ ...prev, [fieldName]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!asset.inventory_number) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.type_id) newErrors.type_id = 'Обязательное поле';
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';
    if (!asset.responsible_person) newErrors.responsible_person = 'Обязательное поле';
    if (!asset.department_id) newErrors.department_id = 'Обязательное поле';
    if (!asset.purchase_date) newErrors.purchase_date = 'Обязательное поле';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const dataToSend = {
        ...asset,
        ...dynamicFields
      };

      await axios.post(`${API_BASE_URL}/api/assets`, dataToSend, { withCredentials: true });
      alert('Актив успешно добавлен');
      navigate('/assets');
    } catch (error) {
      console.error('Ошибка при добавлении актива:', error);
      let errorMessage = 'Неизвестная ошибка';
      if (error.response) {
        if (error.response.data && error.response.data.error) {
          errorMessage = error.response.data.error;
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        } else {
          errorMessage = `Ошибка ${error.response.status}: ${error.response.statusText}`;
        }
      } else if (error.request) {
        errorMessage = 'Нет ответа от сервера';
      } else {
        errorMessage = error.message;
      }
      alert(`Ошибка при добавлении актива: ${errorMessage}`);
    }
  };

  const handleOpenCancelDialog = () => {
    const isFormTouched =
      asset.serial_number ||
      asset.inventory_number ||
      asset.type_id ||
      asset.brand ||
      asset.model ||
      Object.keys(dynamicFields).some(key => dynamicFields[key]) ||
      asset.status_id ||
      asset.actual_user ||
      asset.responsible_person ||
      asset.department_id ||
      asset.room ||
      asset.purchase_date ||
      asset.comments;

    if (isFormTouched) {
      setOpenCancelDialog(true);
    } else {
      navigate('/assets');
    }
  };

  const handleConfirmCancel = () => {
    setOpenCancelDialog(false);
    navigate('/assets');
  };

  const handleCloseCancelDialog = () => {
    setOpenCancelDialog(false);
  };

  const renderDynamicField = (propertyName) => {
    const fieldName = mapPropertyNameToAssetField(propertyName);
    const value = dynamicFields[fieldName] || '';

    let fieldType = 'text';
    if (propertyName.toLowerCase().includes('дата')) {
      fieldType = 'date';
    }

    return (
      <TextField
        key={fieldName}
        label={propertyName}
        name={fieldName}
        value={value}
        onChange={handleDynamicFieldChange(fieldName)}
        fullWidth
        margin="dense"
        sx={{ mb: 1 }}
      />
    );
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '10px' }}>
      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 1.5 }}>
        <Typography variant="h5">Добавить</Typography>

            <Grid container spacing={2}>
              {/* Серийный номер */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Серийный номер"
                  name="serial_number"
                  value={editedAsset.serial_number || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Инвентарный номер */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Инвентарный номер *"
                  name="inventory_number"
                  value={editedAsset.inventory_number || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!editErrors.inventory_number}
                  helperText={editErrors.inventory_number}
                />
              </Grid>

              {/* Категория (Тип устройства) */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.type_id}>
                  <InputLabel id="edit-type-label">Категория *</InputLabel>
                  <Select
                    labelId="edit-type-label"
                    label="Категория *"
                    name="type_id"
                    value={editedAsset.type_id || ''}
                    onChange={handleEditChange}
                  >
                    {types.map(cat => (
                      <MenuItem key={cat.id} value={cat.id}>
                        {cat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.type_id && (
                    <FormHelperText>{editErrors.type_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Фирма */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Фирма"
                  name="brand"
                  value={editedAsset.brand || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Модель */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Модель"
                  name="model"
                  value={editedAsset.model || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Конфигурация */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Конфигурация (Процессор/DDR/HDD)"
                  name="type"
                  value={editedAsset.type || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Динамические поля, полученные из type_properties */}
              {typeProperties.map((property) => {
                const fieldName = mapPropertyNameToAssetField(property.name);
                const value = dynamicFields[fieldName] || '';
                return (
                  <Grid item xs={12} sm={6} key={property.id}>
                    <TextField
                      label={property.name}
                      name={fieldName}
                      value={value}
                      onChange={handleDynamicFieldChange(fieldName)}
                      fullWidth
                      margin="normal"
                    />
                  </Grid>
                );
              })}

              {/* Статус */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.status_id}>
                  <InputLabel id="edit-status-label">Статус *</InputLabel>
                  <Select
                    labelId="edit-status-label"
                    label="Статус *"
                    name="status_id"
                    value={editedAsset.status_id || ''}
                    onChange={handleEditChange}
                  >
                    {statuses.map(stat => (
                      <MenuItem key={stat.id} value={stat.id}>
                        {stat.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.status_id && (
                    <FormHelperText>{editErrors.status_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Фактический пользователь */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Фактический пользователь"
                  name="actual_user"
                  value={editedAsset.actual_user || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Ответственный */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.responsible_person}>
                  <InputLabel id="edit-responsible-person-label">Ответственный *</InputLabel>
                  <Select
                    labelId="edit-responsible-person-label"
                    label="Ответственный *"
                    name="responsible_person"
                    value={editedAsset.responsible_person || ''}
                    // <<<--- ИЗМЕНЕНО: Используем новую функцию
                    onChange={handleEditResponsiblePersonChange}
                  >
                    {employees.map(emp => (
                      <MenuItem key={emp.id} value={emp.id}>
                        {emp.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.responsible_person && (
                    <FormHelperText>{editErrors.responsible_person}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Отдел (подразделение) */}
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth margin="normal" error={!!editErrors.department_id}>
                  <InputLabel id="edit-department-label">Отдел (подразделение) *</InputLabel>
                  <Select
                    labelId="edit-department-label"
                    label="Отдел (подразделение) *"
                    name="department_id"
                    value={editedAsset.department_id || ''}
                    onChange={handleEditChange}
                    // <<<--- ИЗМЕНЕНО: Поле заблокировано для редактирования
                    disabled
                    style={{
                      backgroundColor: '#f5f5f5',
                      color: 'rgba(0, 0, 0, 0.87)',
                      cursor: 'not-allowed'
                    }}
                  >
                    {departments.map(dept => (
                      <MenuItem key={dept.id} value={dept.id}>
                        {dept.name}
                      </MenuItem>
                    ))}
                  </Select>
                  {editErrors.department_id && (
                    <FormHelperText>{editErrors.department_id}</FormHelperText>
                  )}
                </FormControl>
              </Grid>

              {/* Помещение */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Помещение"
                  name="room"
                  value={editedAsset.room || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                />
              </Grid>

              {/* Дата покупки */}
              <Grid item xs={12} sm={6}>
                <TextField
                  label="Дата покупки *"
                  name="purchase_date"
                  type="date"
                  value={editedAsset.purchase_date || ''}
                  onChange={handleEditChange}
                  fullWidth
                  margin="normal"
                  error={!!editErrors.purchase_date}
                  helperText={editErrors.purchase_date}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </Grid>

              {/* Комментарий */}
              <Grid item xs={12}>
                <TextField
                  label="Комментарий"
                  name="comments"
                  value={editedAsset.comments || ''}
                  onChange={handleEditChange}
                  fullWidth
                  multiline
                  rows={4}
                  margin="normal"
                />
              </Grid>
            </Grid>

        <Box display="flex" justifyContent="space-between" mt={1.5} mb={2}>
          <Button
            variant="outlined"
            color="primary"
            onClick={handleOpenCancelDialog}
            disabled={!currentUser || currentUser.role !== 'admin'}
          >
            Отмена
          </Button>
          <Button
            type="submit"
            variant="contained"
            color="success"
            disabled={!currentUser || currentUser.role !== 'admin'}
          >
            Добавить
          </Button>
        </Box>
      </Box>

      <Dialog
        open={openCancelDialog}
        onClose={handleCloseCancelDialog}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Подтвердите отмену"}
        </DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Вы уверены, что хотите отменить добавление? Все введённые данные будут потеряны.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseCancelDialog} color="primary">
            Продолжить заполнение
          </Button>
          <Button onClick={handleConfirmCancel} color="error" autoFocus>
            Отменить
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AddAsset;
