// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Login';
import AssetList from './components/AssetList';
import AssetDetails from './components/AssetDetails';
import Sidebar from './components/Sidebar';
import Header from './components/Header'; // Новый компонент

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  return (
    <Router>
      <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Шапка с именем пользователя */}
        {currentUser && <Header username={currentUser.username} onLogout={() => setCurrentUser(null)} role={currentUser.role} />}

        <div style={{ display: 'flex', flexGrow: 1 }}>
          {/* Меню слева */}
          {currentUser && <Sidebar role={currentUser.role} />}

          {/* Основной контент */}
          <div style={{
            flex: 1,
            backgroundColor: '#f9f9f9',
            padding: '20px'
          }}>
            <Routes>
              {/* Страница входа */}
              <Route path="/login" element={<Login onLogin={handleLogin} />} />

              {/* Главная страница */}
              <Route path="/" element={
                currentUser ? (
                  <AssetList currentUser={currentUser} />
                ) : (
                  <Navigate to="/login" replace />
                )
              } />

              {/* Детальная карточка актива */}
              <Route path="/assets/:id" element={<AssetDetails currentUser={currentUser} />} />

              {/* Добавление актива */}
              <Route path="/assets/new" element={<AddAsset />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
