// frontend/src/components/AssetList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableRow,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AssetList = ({ currentUser }) => {
  const navigate = useNavigate();
  const [assets, setAssets] = useState([]);
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');

  // Загрузка данных при монтировании
  useEffect(() => {
    // Получаем список активов
    axios.get('http://localhost:5000/api/assets/')
      .then(res => setAssets(res.data))
      .catch(err => console.error('Ошибка загрузки активов:', err));

    // Получаем категории
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data));

    // Получаем статусы
    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data));
  }, []);

  // Фильтрация данных
  const filteredAssets = assets.filter(asset => {
    const matchesSearch = asset.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           asset.inventory_number?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory = selectedCategory ? asset.category_id === parseInt(selectedCategory) : true;
    const matchesStatus = selectedStatus ? asset.status_id === parseInt(selectedStatus) : true;

    return matchesSearch && matchesCategory && matchesStatus;
  });

  return (
    <Container maxWidth="lg" style={{ marginTop: '30px' }}>
      <Paper elevation={3} style={{ padding: '20px', backgroundColor: '#fff' }}>
        <Typography variant="h5" gutterBottom>Список активов</Typography>

        {/* Поиск */}
        <Grid container spacing={2} style={{ marginBottom: '20px' }}>
          <Grid item xs={12} sm={6}>
            <TextField
              label="Поиск по названию или инвентарному номеру"
              fullWidth
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </Grid>

          <Grid item xs={12} sm={3}>
            <FormControl fullWidth>
              <InputLabel id="category-filter-label">Фильтр по категории</InputLabel>
              <Select
                labelId="category-filter-label"
                value={selectedCategory}
                label="Категории"
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                <MenuItem value="">Все категории</MenuItem>
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>{cat.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} sm={3}>
            <FormControl fullWidth>
              <InputLabel id="status-filter-label">Фильтр по статусу</InputLabel>
              <Select
                labelId="status-filter-label"
                value={selectedStatus}
                label="Статусы"
                onChange={(e) => setSelectedStatus(e.target.value)}
              >
                <MenuItem value="">Все статусы</MenuItem>
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>{stat.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>

        {/* Кнопка добавления актива (только для администраторов) */}
        {currentUser && currentUser.role === 'admin' && (
          <Button
            variant="contained"
            color="primary"
            onClick={() => navigate('/assets/new')}
            style={{ marginBottom: '20px' }}
          >
            Добавить актива
          </Button>
        )}

        {/* Таблица активов */}
        <TableContainer component={Paper}>
          <Table>
            <TableBody>
              {filteredAssets.length > 0 ? (
                filteredAssets.map((asset) => (
                  <TableRow
                    key={asset.id}
                    hover
                    onClick={() => navigate(`/assets/${asset.id}`)}
                    style={{ cursor: 'pointer' }}
                  >
                    <TableCell>{asset.inventory_number}</TableCell>
                    <TableCell>{asset.name}</TableCell>
                    <TableCell>{asset.location}</TableCell>
                    <TableCell>{asset.status_id}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={4} align="center">Нет записей</TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default AssetList;
