// frontend/src/components/DepartmentList.js

import React, { useEffect, useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Box
} from '@mui/material';
import axios from 'axios';

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);
  const [employeeCount, setEmployeeCount] = useState({});
  const [assetCount, setAssetCount] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);

        // Для каждого отдела загружаем количество сотрудников и оборудования
        const empCounts = {};
        const assetCounts = {};

        for (let dept of deps) {
          const empRes = await axios.get(`http://localhost:5000/api/employees?department_id=${dept.id}`);
          empCounts[dept.id] = empRes.data.length;

          const assetRes = await axios.get(`http://localhost:5000/api/assets?department_id=${dept.id}`);
          assetCounts[dept.id] = assetRes.data.length;
        }

        setEmployeeCount(empCounts);
        setAssetCount(assetCounts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Список отделов
        </Typography>

        <Table size="small">
          <TableBody>
            {departments.map(dept => (
              <TableRow key={dept.id}>
                <TableCell>{dept.name}</TableCell>
                <TableCell align="right">{employeeCount[dept.id] || 0}</TableCell>
                <TableCell align="right">{assetCount[dept.id] || 0}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default DepartmentList;
