// frontend/src/components/Directory.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Table,
  TableBody,
  TableRow,
  TableCell,
  TextField,
  Button,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  Tooltip,
  MenuItem
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import EditIcon from '@mui/icons-material/Edit';
import AddIcon from '@mui/icons-material/Add';

const Directory = () => {
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);

  const [newCategory, setNewCategory] = useState('');
  const [newStatus, setNewStatus] = useState('');
  const [newDepartment, setNewDepartment] = useState('');
  const [newEmployeeName, setNewEmployeeName] = useState('');
  const [newEmployeeDepartmentId, setNewEmployeeDepartmentId] = useState('');

  // Для редактирования
  const [editingItem, setEditingItem] = useState(null);
  const [editName, setEditName] = useState('');
  const [editDeptId, setEditDeptId] = useState('');

  // Загружаем справочные данные
  const fetchAllData = async () => {
    try {
      const [catsRes, statsRes, depsRes, empsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/categories'),
        axios.get('http://localhost:5000/api/statuses'),
        axios.get('http://localhost:5000/api/departments'),
        axios.get('http://localhost:5000/api/employees'),
      ]);

      setCategories(catsRes.data);
      setStatuses(statsRes.data);
      setDepartments(depsRes.data);
      setEmployees(empsRes.data);
    } catch (err) {
      console.error('Ошибка загрузки справочников:', err);
    }
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  // === Добавление записей ===
  const handleAddCategory = async () => {
    if (!newCategory.trim()) return alert('Введите название категории');
    try {
      const res = await axios.post('http://localhost:5000/api/categories', { name: newCategory });
      setCategories([...categories, res.data]);
      setNewCategory('');
    } catch (err) {
      alert('Ошибка при добавлении категории');
      console.error(err);
    }
  };

  const handleAddStatus = async () => {
    if (!newStatus.trim()) return alert('Введите статус');
    try {
      const res = await axios.post('http://localhost:5000/api/statuses', { name: newStatus });
      setStatuses([...statuses, res.data]);
      setNewStatus('');
    } catch (err) {
      alert('Ошибка при добавлении статуса');
      console.error(err);
    }
  };

  const handleAddDepartment = async () => {
    if (!newDepartment.trim()) return alert('Введите отдел');
    try {
      const res = await axios.post('http://localhost:5000/api/departments', { name: newDepartment });
      setDepartments([...departments, res.data]);
      setNewDepartment('');
    } catch (err) {
      alert('Ошибка при добавлении отдела');
      console.error(err);
    }
  };

  const handleAddEmployee = async () => {
    if (!newEmployeeName || !newEmployeeDepartmentId)
      return alert('Введите ФИО и выберите отдел');

    try {
      const res = await axios.post('http://localhost:5000/api/employees', {
        name: newEmployeeName,
        department_id: parseInt(newEmployeeDepartmentId),
      });

      setEmployees([...employees, res.data]);
      setNewEmployeeName('');
      setNewEmployeeDepartmentId('');
    } catch (err) {
      alert('Ошибка при добавлении сотрудника');
      console.error(err);
    }
  };

  // === Удаление записей ===
  const handleDeleteCategory = async (id) => {
    if (!window.confirm('Удалить эту категорию?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/categories/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении категории');
      console.error(err);
    }
  };

  const handleDeleteStatus = async (id) => {
    if (!window.confirm('Удалить этот статус?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/statuses/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении статуса');
      console.error(err);
    }
  };

  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('Удалить этот отдел?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/departments/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении отдела');
      console.error(err);
    }
  };

  const handleDeleteEmployee = async (id) => {
    if (!window.confirm('Удалить этого сотрудника?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/employees/${id}`);
      fetchAllData();
    } catch (err) {
      alert('Ошибка при удалении сотрудника');
      console.error(err);
    }
  };

  // === Редактирование записей ===
  const handleEditClick = (itemType, item) => {
    setEditingItem({ type: itemType, id: item.id, name: item.name, department_id: item.department_id });
    if (itemType === 'employee') {
      setEditName(item.name);
      setEditDeptId(item.department_id);
    } else {
      setEditName(item.name);
    }
  };

  const handleEditSave = async () => {
    const { type, id } = editingItem;

    let url = '';
    let payload = {};
    let updateList = null;

    switch (type) {
      case 'category':
        url = `/api/categories/${id}`;
        payload = { name: editName };
        updateList = setCategories;
        break;
      case 'status':
        url = `/api/statuses/${id}`;
        payload = { name: editName };
        updateList = setStatuses;
        break;
      case 'department':
        url = `/api/departments/${id}`;
        payload = { name: editName };
        updateList = setDepartments;
        break;
      case 'employee':
        url = `/api/employees/${id}`;
        payload = { name: editName, department_id: editDeptId };
        updateList = setEmployees;
        break;
      default:
        return;
    }

    try {
      const res = await axios.put(`http://localhost:5000${url}`, payload);
      const updatedItem = res.data;

      updateList(prev => prev.map(item =>
        item.id === id ? updatedItem : item
      ));

      setEditName('');
      setEditDeptId('');
      setEditingItem(null);
    } catch (err) {
      alert('Ошибка при редактировании');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px' }}>
        {/* Категории */}
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Typography variant="h6" style={{lineHeight: '3'}}>Категории</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новая категория"
                value={newCategory}
                onChange={(e) => setNewCategory(e.target.value)}
                size="small"
                fullWidth
              />
              <Tooltip title="Добавить категорию">
                <IconButton color="success" onClick={handleAddCategory}>
                  <AddIcon />
                </IconButton>
              </Tooltip>
            </Box>
            <Table size="small">
              <TableBody>
                {categories.map(cat => (
                  <TableRow
                    key={cat.id}
                    hover
                    sx={{
                      '&:hover .edit-delete-buttons': {
                        opacity: 1,
                        visibility: 'visible'
                      },
                      transition: 'all 0.2s ease',
                      cursor: 'pointer'
                    }}
                  >
                    <TableCell>{cat.name}</TableCell>
                    <TableCell align="right">
                      <Box className="edit-delete-buttons" sx={{
                        display: 'flex',
                        gap: 1,
                        alignItems: 'center',
                        visibility: 'hidden',
                        opacity: 0,
                        transition: 'opacity 0.2s, visibility 0.2s',
                      }}>
                        <Tooltip title="Редактировать">
                          <IconButton color="primary" onClick={() => handleEditClick('category', cat)}>
                            <EditIcon />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Удалить">
                          <IconButton color="error" onClick={() => handleDeleteCategory(cat.id)}>
                            <CloseIcon />
                          </IconButton>
                        </Tooltip>
                      </Box>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Статусы */}
          <Grid item xs={6}>
            <Typography variant="h6" style={{lineHeight: '3'}}>Статусы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый статус"
                value={newStatus}
                onChange={(e) => setNewStatus(e.target.value)}
                size="small"
                fullWidth
              />
              <Tooltip title="Добавить статус">
                <IconButton color="success" onClick={handleAddStatus}>
                  <AddIcon />
                </IconButton>
              </Tooltip>
            </Box>
            <Table size="small">
              <TableBody>
                {statuses.map(stat => (
                  <TableRow
                    key={stat.id}
                    hover
                    sx={{
                      '&:hover .edit-delete-buttons': {
                        opacity: 1,
                        visibility: 'visible'
                      },
                      transition: 'all 0.2s ease',
                      cursor: 'pointer'
                    }}
                  >
                    <TableCell>{stat.name}</TableCell>
                    <TableCell align="right">
                      <Box className="edit-delete-buttons" sx={{
                        display: 'flex',
                        gap: 1,
                        visibility: 'hidden',
                        opacity: 0,
                        transition: 'opacity 0.2s, visibility 0.2s',
                      }}>
                        <Tooltip title="Редактировать">
                          <IconButton color="primary" onClick={() => handleEditClick('status', stat)}>
                            <EditIcon />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Удалить">
                          <IconButton color="error" onClick={() => handleDeleteStatus(stat.id)}>
                            <CloseIcon />
                          </IconButton>
                        </Tooltip>
                      </Box>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Отделы */}
          <Grid item xs={6}>
            <Typography variant="h6" style={{lineHeight: '3'}}>Отделы</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="Новый отдел"
                value={newDepartment}
                onChange={(e) => setNewDepartment(e.target.value)}
                size="small"
                fullWidth
              />
            <Tooltip title="Добавить отдел">
                <IconButton color="success" onClick={handleAddDepartment}>
                  <AddIcon />
                </IconButton>
              </Tooltip>
            </Box>
            <Table size="small">
              <TableBody>
                {departments.map(dept => (
                  <TableRow
                    key={dept.id}
                    hover
                    sx={{
                      '&:hover .edit-delete-buttons': {
                        opacity: 1,
                        visibility: 'visible'
                      },
                      transition: 'all 0.2s ease',
                      cursor: 'pointer'
                    }}
                  >
                    <TableCell>{dept.name}</TableCell>
                    <TableCell align="right">
                      <Box className="edit-delete-buttons" sx={{
                        display: 'flex',
                        gap: 1,
                        visibility: 'hidden',
                        opacity: 0,
                        transition: 'opacity 0.2s, visibility 0.2s',
                      }}>
                        <Tooltip title="Редактировать">
                          <IconButton color="primary" onClick={() => handleEditClick('department', dept)}>
                            <EditIcon />
                          </IconButton>
                        </Tooltip>
                        <Tooltip title="Удалить">
                          <IconButton color="error" onClick={() => handleDeleteDepartment(dept.id)}>
                            <CloseIcon />
                          </IconButton>
                        </Tooltip>
                      </Box>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Grid>

          {/* Сотрудники */}
          <Grid item xs={6}>
            <Typography variant="h6" >Сотрудники</Typography>
            <Box display="flex" gap={2} mb={2}>
              <TextField
                label="ФИО"
                value={newEmployeeName}
                onChange={(e) => setNewEmployeeName(e.target.value)}
                size="small"
                fullWidth
              />
              <TextField
                select
                label="Отдел"
                value={newEmployeeDepartmentId}
                onChange={(e) => setNewEmployeeDepartmentId(e.target.value)}
                size="small"
                sx={{ minWidth: 150 }}
              >
                {departments.map(d => (
                  <MenuItem key={d.id} value={d.id}>{d.name}</MenuItem>
                ))}
              </TextField>
              <Tooltip title="Добавить сотрудника">
                <IconButton color="success" onClick={handleAddEmployee}>
                  <AddIcon />
                </IconButton>
              </Tooltip>
            </Box>
            <Table size="small">
              <TableBody>
                {employees.map(emp => {
                  const dept = departments.find(d => d.id === emp.department_id);
                  return (
                    <TableRow
                      key={emp.id}
                      hover
                      sx={{
                        '&:hover .edit-delete-buttons': {
                          opacity: 1,
                          visibility: 'visible'
                        },
                        transition: 'all 0.2s ease',
                        cursor: 'pointer'
                      }}
                    >
                      <TableCell>{emp.name}</TableCell>
                      <TableCell>{dept?.name || 'Не указано'}</TableCell>
                      <TableCell align="right">
                        <Box className="edit-delete-buttons" sx={{
                          display: 'flex',
                          gap: 1,
                          visibility: 'hidden',
                          opacity: 0,
                          transition: 'opacity 0.2s, visibility 0.2s',
                        }}>
                          <Tooltip title="Редактировать">
                            <IconButton color="primary" onClick={() => handleEditClick('employee', emp)}>
                              <EditIcon />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Удалить">
                            <IconButton color="error" onClick={() => handleDeleteEmployee(emp.id)}>
                              <CloseIcon />
                            </IconButton>
                          </Tooltip>
                        </Box>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </Grid>
        </Grid>
      </Paper>

      {/* Модальное окно редактирования */}
      {editingItem && (
        <Dialog open onClose={() => setEditingItem(null)}>
          <DialogTitle>Редактировать {editingItem.type}</DialogTitle>
          <DialogContent>
            {['category', 'status', 'department'].includes(editingItem.type) && (
              <TextField
                label="Название"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                fullWidth
                margin="normal"
              />
            )}

            {editingItem.type === 'employee' && (
              <>
                <TextField
                  label="ФИО"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  select
                  label="Отдел"
                  value={editDeptId}
                  onChange={(e) => setEditDeptId(parseInt(e.target.value))}
                  fullWidth
                  margin="normal"
                >
                  {departments.map(d => (
                    <MenuItem key={d.id} value={d.id}>
                      {d.name}
                    </MenuItem>
                  ))}
                </TextField>
              </>
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setEditingItem(null)}>Отмена</Button>
            <Button variant="contained" color="success" onClick={handleEditSave}>
              Сохранить
            </Button>
          </DialogActions>
        </Dialog>
      )}
    </Container>
  );
};

export default Directory;
