// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  Box,
  TextField,
  Button,
  Snackbar,
  Alert,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react'; // Исправленный импорт

const navigate = useNavigate();
const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const [asset, setAsset] = useState(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  useEffect(() => {
    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => setAsset(res.data))
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        setSnackbar({
          open: true,
          message: 'Не удалось загрузить данные актива',
          severity: 'error'
        });
      });
  }, [id]);

  if (!asset) {
    return <Box p={3}>Загрузка...</Box>;
  }

  const handlePrintQR = () => {
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <html>
        <head><title>Печать QR-кода</title></head>
        <body style="text-align:center;">
          <h3>${asset.inventory_number}</h3>
          ${document.getElementById('qrcode')?.outerHTML || ''}
          <script>window.print();</script>
        </body>
      </html>
    `);
    printWindow.document.close();
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', backgroundColor: '#fff' }}>
        <Typography variant="h5" gutterBottom>{asset.name}</Typography>

        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер"
              name="inventory_number"
              value={asset.inventory_number || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
          </Grid>

          <Grid item xs={6}>
            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              label="Статус"
              name="status_id"
              value={asset.status_id || ''}
              disabled={!currentUser || currentUser.role !== 'admin'}
              fullWidth
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* QR-код */}
        <Box mt={3} display="flex" justifyContent="center">
          <div id="qrcode">
            <QRCodeSVG
              value={asset.inventory_number || 'IT-Актив'}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
        </Box>

        {/* Кнопки управления */}
        <Box mt={3} display="flex" justifyContent="space-between">
          <Button variant="outlined" onClick={() => navigate(-1)}>
          Назад к списку
          </Button>

          {currentUser && currentUser.role === 'admin' && (
            <Button variant="contained" color="primary" onClick={handleSave}>
              Сохранить изменения
            </Button>
          )}

          <Button variant="contained" color="info" onClick={handlePrintQR}>
            Печать QR-кода
          </Button>
        </Box>
      </Paper>

      {/* Snackbar для уведомлений */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default AssetDetails;
