import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [employees, setEmployees] = useState([]);

  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState('inventory_number'); // Столбец по умолчанию
  const [sortOrder, setSortOrder] = useState('asc'); // Направление сортировки
  const navigate = useNavigate();

  // Загрузка всех данных
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetsRes = await axios.get('http://localhost:5000/api/assets');
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const statsRes = await axios.get('http://localhost:5000/api/statuses');
        const empsRes = await axios.get('http://localhost:5000/api/employees');

        setAssets(assetsRes.data);
        setDepartments(depsRes.data);
        setStatuses(statsRes.data);
        setEmployees(empsRes.data);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  // Форматирование даты в DD.MM.YYYY
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    if (isNaN(date)) return '';

    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();

    return `${day}.${month}.${year}`;
  };

  // Форматирование ФИО → Иванов И.И.
  const formatEmployeeName = (employeeId) => {
    if (!employeeId) return '-';

    const employee = employees.find(emp => emp.id === employeeId);
    if (!employee) return '-';

    const parts = employee.name.split(' ');
    const surname = parts[0] || '';
    const firstInitial = parts[1]?.charAt(0).toUpperCase() || '';
    const patronymicInitial = parts[2]?.charAt(0).toUpperCase() || '';

    return `${surname} ${firstInitial}.${patronymicInitial}.`;
  };

  // Получение имени статуса по ID
  const getStatusName = (statusId) => {
    const status = statuses.find(stat => stat.id === statusId);
    return status?.name || 'Не указан';
  };

  // Получение имени отдела по ID
  const getDepartmentName = (departmentId) => {
    const department = departments.find(d => d.id === departmentId);
    return department?.name || 'Не указан';
  };

  // Фильтрация активов по поисковому запросу (включая помещение)
  const filteredAssets = assets.filter(asset => {
    const searchLower = searchTerm.toLowerCase();

    const assetFieldsToSearch = [
      asset.inventory_number,
      `${asset.type} ${asset.brand} ${asset.model}`,
      getStatusName(asset.status_id),
      formatEmployeeName(asset.responsible_person),
      getDepartmentName(asset.department_id),
      asset.room || '', // Поле помещения
      formatDate(asset.purchase_date),
    ].join(' ').toLowerCase();

    return assetFieldsToSearch.includes(searchLower);
  });

  // Сортировка активов
  const sortedAssets = [...filteredAssets].sort((a, b) => {
    let comparison = 0;

    switch (sortColumn) {
      case 'inventory_number':
        comparison = a.inventory_number.localeCompare(b.inventory_number, undefined, { numeric: true });
        break;
      case 'purchase_date':
        comparison = new Date(a.purchase_date) - new Date(b.purchase_date);
        break;
      case 'room':
        const roomA = (a.room || '').toLowerCase();
        const roomB = (b.room || '').toLowerCase();
        comparison = roomA.localeCompare(roomB);
        break;
      default:
        return 0;
    }

    if (sortOrder === 'desc') {
      comparison = -comparison;
    }

    return comparison;
  });

  // Обработчик клика на заголовок столбца
  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortOrder('asc');
    }
  };

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom style={{ textAlign: 'center', fontSize: '30px' }}>
          <strong>Список оборудования</strong>
        </Typography>

        {/* Поисковая строка */}
        <TextField
          label="Поиск"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: '16px' }}
        />

        {/* Кнопка добавления актива */}
        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/assets/new')}
          style={{ marginBottom: '16px' }}
        >
          Новое оборудование
        </Button>

        {/* Таблица активов */}
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell
                style={{
                  fontSize: '12px',
                  fontWeight: sortColumn === 'inventory_number' ? 'bold' : 'normal',
                  color: "#1976d2",
                  cursor: 'pointer'
                }}
                onClick={() => handleSort('inventory_number')}
              >
                Инвентарный номер{' '}
                {sortColumn === 'inventory_number' && (
                  <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>
                )}
              </TableCell>
              <TableCell style={{ fontSize: '12px' }}>Наименование</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Статус</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Ответственный</TableCell>
              <TableCell style={{ fontSize: '12px' }}>Отдел</TableCell>
              <TableCell
                style={{
                  fontSize: '12px',
                  fontWeight: sortColumn === 'room' ? 'bold' : 'normal',
                  color: "#1976d2",
                  cursor: 'pointer'
                }}
                onClick={() => handleSort('room')}
              >
                Помещение{' '}
                {sortColumn === 'room' && (
                  <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>
                )}
              </TableCell>
              <TableCell
                style={{
                  fontSize: '12px',
                  fontWeight: sortColumn === 'purchase_date' ? 'bold' : 'normal',
                  color: "#1976d2",
                  cursor: 'pointer'
                }}
                onClick={() => handleSort('purchase_date')}
              >
                Дата покупки{' '}
                {sortColumn === 'purchase_date' && (
                  <span>{sortOrder === 'asc' ? '↑' : '↓'}</span>
                )}
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {sortedAssets.map((asset) => (
              <TableRow
                key={asset.id}
                hover
                onClick={() => navigate(`/assets/${asset.id}`)}
                style={{ cursor: 'pointer' }}
              >
                <TableCell style={{ padding: '20px' }}>{asset.inventory_number}</TableCell>
                <TableCell>{`${asset.type || ''} ${asset.brand || ''} ${asset.model || ''}`.trim() || '-'}</TableCell>
                <TableCell>{getStatusName(asset.status_id)}</TableCell>
                <TableCell>{formatEmployeeName(asset.responsible_person)}</TableCell>
                <TableCell>{getDepartmentName(asset.department_id)}</TableCell>
                <TableCell>{asset.room || '-'}</TableCell>
                <TableCell>{asset.purchase_date ? formatDate(asset.purchase_date) : '-'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default AssetList;
