// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';

const AssetDetails = ({ currentUser }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    name: '',
    category_id: '',
    department_id: '',
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: '',
  });

  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [errors, setErrors] = useState({});
  const [selectedEmployeeId, setSelectedEmployeeId] = useState('');

  // Загружаем актив по ID
  useEffect(() => {
    if (!id || id === 'new') return;

    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => {
        const data = res.data;
        setAsset(data);
        setSelectedEmployeeId(data.responsible_person_id || '');
      })
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        alert('Не удалось загрузить актив');
        navigate('/');
      });
  }, [id]);

  // Загружаем справочные данные
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [catsRes, statsRes, depsRes, empsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/categories'),
          axios.get('http://localhost:5000/api/statuses'),
          axios.get('http://localhost:5000/api/departments'),
          axios.get('http://localhost:5000/api/employees')
        ]);

        setCategories(catsRes.data);
        setStatuses(statsRes.data);
        setDepartments(depsRes.data);
        setEmployees(empsRes.data);
      } catch (err) {
        console.error('Ошибка загрузки справочных данных:', err);
      }
    };

    fetchData();
  }, []);

  // Обработчик изменения полей
  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset(prev => ({ ...prev, [name]: value }));
  };

  // Обработчик выбора сотрудника
  const handleEmployeeSelect = (e) => {
    const empId = e.target.value;
    setSelectedEmployeeId(empId);

    // Находим сотрудника
    const selectedEmp = employees.find(emp => emp.id === parseInt(empId));
    if (selectedEmp) {
      setAsset(prev => ({
        ...prev,
        responsible_person: selectedEmp.name,
        department_id: selectedEmp.department_id,
      }));
    }
  };

  // Сохранение изменений
  const handleSave = async () => {
    const newErrors = {};

    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Инвентарный номер обязателен';
    if (!asset.name?.trim()) newErrors.name = 'Наименование обязательно';
    if (!asset.category_id) newErrors.category_id = 'Категория обязательна';
    if (!asset.status_id) newErrors.status_id = 'Статус обязателен';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      if (id === 'new') {
        const res = await axios.post('http://localhost:5000/api/assets', asset);
        navigate(`/assets/${res.data.id}`);
      } else {
        await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
        alert('Данные успешно обновлены');
      }
    } catch (err) {
      alert('Ошибка при сохранении данных');
      console.error(err);
    }
  };

  // Удаление актива
  const handleDelete = async () => {
    if (!window.confirm('Удалить этот актив?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/assets/${id}`);
      navigate('/');
    } catch (err) {
      alert('Ошибка при удалении актива');
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Редактировать актив: {asset.name}
        </Typography>

        <Grid container spacing={2}>
          {/* Левая колонка */}
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
            />
            <TextField
              label="Наименование"
              name="name"
              value={asset.name || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
            <TextField
              select
              label="Ответственный сотрудник"
              value={selectedEmployeeId}
              onChange={handleEmployeeSelect}
              fullWidth
              margin="normal"
            >
              {employees.map(emp => (
                <MenuItem key={emp.id} value={emp.id}>
                  {emp.name}
                </MenuItem>
              ))}
            </TextField>
          </Grid>

          {/* Правая колонка */}
          <Grid item xs={6}>
            <TextField
              select
              label="Категория *"
              name="category_id"
              value={asset.category_id || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.category_id}
              helperText={errors.category_id}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              {categories.map(cat => (
                <MenuItem key={cat.id} value={cat.id}>
                  {cat.name}
                </MenuItem>
              ))}
            </TextField>

            <TextField
              select
              label="Статус *"
              name="status_id"
              value={asset.status_id || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.status_id}
              helperText={errors.status_id}
            >
              {statuses.map(stat => (
                <MenuItem key={stat.id} value={stat.id}>
                  {stat.name}
                </MenuItem>
              ))}
            </TextField>

            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />

            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              disabled={!currentUser || currentUser.role !== 'admin'}
            />
          </Grid>
        </Grid>

        <Box mt={3} display="flex" justifyContent="space-between" alignItems="center">
          <Button variant="outlined" onClick={() => navigate(-1)}>Назад</Button>
          <Box>
            <Button
              variant="contained"
              color="success"
              onClick={handleSave}
              sx={{ mr: 2 }}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              Сохранить изменения
            </Button>
            <Button
              variant="contained"
              color="error"
              onClick={handleDelete}
              disabled={!currentUser || currentUser.role !== 'admin'}
            >
              Удалить
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
