import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assetCounts, setAssetCounts] = useState({}); // Кол-во активов на сотрудника
  const [totalAssetsByDept, setTotalAssetsByDept] = useState({}); // Сумма активов по отделам

  // === Загрузка данных ===
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем сотрудников
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const emps = empsRes.data;
        setEmployees(emps);

        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);

        // Подсчитываем оборудование для каждого сотрудника
        const assetCounts = {};
        const totalAssetsByDept = {};

        for (let emp of emps) {
          const res = await axios.get(`http://localhost:5000/api/assets?responsible_person=${emp.name}`);
          const count = res.data.length;
          assetCounts[emp.id] = count;

          // Добавляем к общему количеству по отделу
          if (!totalAssetsByDept[emp.department_id]) {
            totalAssetsByDept[emp.department_id] = 0;
          }
          totalAssetsByDept[emp.department_id] += count;
        }

        setAssetCounts(assetCounts);
        setTotalAssetsByDept(totalAssetsByDept);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          Сотрудники
        </Typography>

        {/* Отображаем сотрудников по отделам */}
        {departments.map(dept => {
          const deptEmployees = employees.filter(emp => emp.department_id === dept.id);
          const totalAssets = totalAssetsByDept[dept.id] || 0;
            if (deptEmployees.length === 0) {
              return null; // Не отображаем пустые отделы
            }
          return (
            <div key={dept.id}>
              <Typography
                variant="h6"
                gutterBottom
                style={{ fontWeight: 'bold', textAlign: 'center' }}
              >
                {dept.name}
              </Typography>

              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>ФИО</TableCell>
                    <TableCell align="right">Оборудования</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {deptEmployees.map(emp => (
                    <TableRow key={emp.id}>
                      <TableCell>{emp.name}</TableCell>
                      <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell>
                    </TableRow>
                  ))}

                  {/* Итоговая строка с суммой оборудования */}
                  <TableRow sx={{ backgroundColor: '#f0f0f0' }}>
                    <TableCell><strong>Всего</strong></TableCell>
                    <TableCell align="right"><strong>{totalAssetsByDept[dept.id] || 0}</strong></TableCell>
                  </TableRow>
                </TableBody>
              </Table>
            </div>
          );
        })}
      </Paper>
    </Container>
  );
};

export default EmployeeList;
