// frontend/src/components/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemButton, ListItemIcon, ListItemText } from '@mui/material';
import HomeIcon from '@mui/icons-material/Home';
import DevicesIcon from '@mui/icons-material/Devices';
import BusinessIcon from '@mui/icons-material/Business';
import PeopleIcon from '@mui/icons-material/People';

const Sidebar = ({ role }) => {
  return (
    <div style={{ width: 250, backgroundColor: '#f4f4f4', height: '100vh' }}>
      <List>
        <ListItem disablePadding>
          <ListItemButton component="a" href="/">
            <ListItemIcon><HomeIcon /></ListItemIcon>
            <ListItemText primary="Главная" />
          </ListItemButton>
        </ListItem>
        <ListItem disablePadding>
          <ListItemButton component="a" href="/assets">
            <ListItemIcon><DevicesIcon /></ListItemIcon>
            <ListItemText primary="Оборудование" />
          </ListItemButton>
        </ListItem>
        <ListItem disablePadding>
          <ListItemButton component="a" href="/departments">
            <ListItemIcon><BusinessIcon /></ListItemIcon>
            <ListItemText primary="Отделы" />
          </ListItemButton>
        </ListItem>
        <ListItem disablePadding>
          <ListItemButton component="a" href="/employees">
            <ListItemIcon><PeopleIcon /></ListItemIcon>
            <ListItemText primary="Сотрудники" />
          </ListItemButton>
        </ListItem>
      </List>
    </div>
  );
};

export default Sidebar;
