// frontend/src/components/ChangesList.js

import React, { useEffect, useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableRow,
  Box,
} from '@mui/material';

const ChangesList = () => {
  const [changes, setChanges] = useState([]);

  // Загрузка истории изменений
  useEffect(() => {
    axios.get('http://localhost:5000/api/changes')
      .then(res => setChanges(res.data))
      .catch(err => console.error('Ошибка загрузки истории:', err));
  }, []);

  return (
    <Container maxWidth="lg">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Typography variant="h5" gutterBottom>
          История изменений
        </Typography>

        {/* Таблица изменений */}
        <Table size="small">
          <TableBody>
            {changes.length > 0 ? (
              changes.map((change) => (
                <TableRow key={change.id}>
                  <TableCell>
                    <strong>Дата:</strong> {change.changed_at}<br />
                    <strong>Оборудование:</strong> {change.full_name || change.inventory_number}<br />
                    <strong>Поле:</strong> {change.field}<br />
                    <strong>Было:</strong> {change.old_value || '-'}<br />
                    <strong>Стало:</strong> {change.new_value || '-'}<br />
                    <strong>Кто изменил:</strong> {change.changed_by || 'Неизвестный пользователь'}
                  </TableCell>
                </TableRow>
              ))
            ) : (
              <TableRow>
                <TableCell align="center">Нет записей об изменениях</TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default ChangesList;
