// frontend/src/components/AssetDetails.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Grid,
  TextField,
  Button,
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const AssetDetails = () => {
  const { id } = useParams(); // Получаем id из URL
  const navigate = useNavigate();
  const [asset, setAsset] = useState({
    serial_number: '',
    inventory_number: '',
    type: '',         // Новое поле
    brand: '',        // Обязательное
    model: '',        // Обязательное
    category_id: '',
    location: '',
    responsible_person: '',
    actual_user: '',
    comments: '',
    status_id: ''
  });
  const [categories, setCategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [errors, setErrors] = useState({});

  // Загружаем актив по ID при монтировании
  useEffect(() => {
    if (!id || id === 'new') return;

    axios.get(`http://localhost:5000/api/assets/${id}`)
      .then(res => {
        setAsset(res.data);
      })
      .catch(err => {
        console.error('Ошибка загрузки актива:', err);
        alert('Не удалось загрузить актив');
        navigate('/');
      });

    // Загружаем категории и статусы
    axios.get('http://localhost:5000/api/categories')
      .then(res => setCategories(res.data))
      .catch(err => console.error('Ошибка загрузки категорий:', err));

    axios.get('http://localhost:5000/api/statuses')
      .then(res => setStatuses(res.data))
      .catch(err => console.error('Ошибка загрузки статусов:', err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAsset({ ...asset, [name]: value });
  };

  const handleSave = async () => {
    // Проверка обязательных полей
    const newErrors = {};
    if (!asset.inventory_number?.trim()) newErrors.inventory_number = 'Обязательное поле';
    if (!asset.type?.trim()) newErrors.type = 'Обязательное поле';
    if (!asset.brand?.trim()) newErrors.brand = 'Обязательное поле';
    if (!asset.model?.trim()) newErrors.model = 'Обязательное поле';
    if (!asset.category_id) newErrors.category_id = 'Обязательное поле';
    if (!asset.status_id) newErrors.status_id = 'Обязательное поле';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      alert('Пожалуйста, заполните все обязательные поля');
      return;
    }

    try {
      await axios.put(`http://localhost:5000/api/assets/${id}`, asset);
      alert('Данные успешно обновлены');
    } catch (err) {
      alert('Ошибка при сохранении изменений');
      console.error(err);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Вы уверены, что хотите удалить этот актив?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/assets/${id}`);
      alert('Актив успешно удален');
      navigate('/');
    } catch (err) {
      alert('Ошибка при удалении актива');
      console.error(err);
    }
  };

  const handlePrintQRCode = () => {
    if (!asset.inventory_number) return;

    const printWindow = window.open('', '_blank', 'width=600,height=400');

    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <style>
            body {
              margin: 0;
              padding: 0;
              display: flex;
              justify-content: center;
              align-items: center;
              height: 100vh;
              background-color: #ffffff;
              font-family: Arial, sans-serif;
            }
            .qr-container {
              text-align: center;
            }
            svg {
              max-width: 100%;
              height: auto;
            }
          </style>
        </head>
        <body>
          <div class="qr-container">
            <!-- Инвентарный номер над QR -->
            <p style="font-size: 18px; margin: 0;">${asset.inventory_number}</p>

            <!-- QR-код -->
            <svg xmlns="http://www.w3.org/2000/svg" width="200" height="200">
              <rect width="100%" height="100%" fill="#ffffff" />
              <foreignObject width="100%" height="100%">
                <div xmlns="http://www.w3.org/1999/xhtml">
                  <img src="data:image/svg+xml;base64,${btoa(
                    new XMLSerializer().serializeToString(
                      document.querySelector('#qrcode').querySelector('svg')
                    )
                  )}" alt="QR Code" />
                </div>
              </foreignObject>
            </svg>

            <!-- Наименование под QR -->
            <p style="font-size: 18px; margin: 0; margin-top: 10px;">
              ${asset.type} ${asset.brand} ${asset.model}
            </p>
          </div>
        </body>
      </html>
    `);

    setTimeout(() => {
      printWindow.print();
      printWindow.close();
    }, 500);
  };

  if (!asset) {
    return (
      <Container maxWidth="md">
        <Paper elevation={3} style={{ padding: '20px' }}>
          <Typography variant="h5">Актив не найден</Typography>
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад к списку
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px' }}>
        {/* Шапка с полным названием */}
        <Typography variant="h5" gutterBottom>
          Редактировать актив: {asset.type} {asset.brand} {asset.model}
        </Typography>

        <Grid container spacing={2}>
          {/* Левая колонка */}
          <Grid item xs={6}>
            <TextField
              label="Серийный номер"
              name="serial_number"
              value={asset.serial_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
            <TextField
              label="Инвентарный номер *"
              name="inventory_number"
              value={asset.inventory_number || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.inventory_number}
              helperText={errors.inventory_number}
            />
            <TextField
              label="Тип *"
              name="type"
              value={asset.type || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.type}
              helperText={errors.type}
            />
            <TextField
              label="Фирма *"
              name="brand"
              value={asset.brand || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.brand}
              helperText={errors.brand}
            />
            <TextField
              label="Модель *"
              name="model"
              value={asset.model || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
              error={!!errors.model}
              helperText={errors.model}
            />
            <TextField
              label="Местоположение"
              name="location"
              value={asset.location || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />
          </Grid>

          {/* Правая колонка */}
          <Grid item xs={6}>
            <FormControl fullWidth margin="normal" error={!!errors.category_id}>
              <InputLabel id="category-label">Категория *</InputLabel>
              <Select
                labelId="category-label"
                label="Категория *"
                name="category_id"
                value={asset.category_id || ''}
                onChange={handleChange}
              >
                {categories.map(cat => (
                  <MenuItem key={cat.id} value={cat.id}>
                    {cat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.category_id && (
                <Typography color="error" variant="caption">
                  {errors.category_id}
                </Typography>
              )}
            </FormControl>

            <FormControl fullWidth margin="normal" error={!!errors.status_id}>
              <InputLabel id="status-label">Статус *</InputLabel>
              <Select
                labelId="status-label"
                label="Статус *"
                name="status_id"
                value={asset.status_id || ''}
                onChange={handleChange}
              >
                {statuses.map(stat => (
                  <MenuItem key={stat.id} value={stat.id}>
                    {stat.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.status_id && (
                <Typography color="error" variant="caption">
                  {errors.status_id}
                </Typography>
              )}
            </FormControl>

            <TextField
              label="Ответственный"
              name="responsible_person"
              value={asset.responsible_person || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="Фактический пользователь"
              name="actual_user"
              value={asset.actual_user || ''}
              onChange={handleChange}
              fullWidth
              margin="normal"
            />

            <TextField
              label="Комментарии"
              name="comments"
              value={asset.comments || ''}
              onChange={handleChange}
              fullWidth
              multiline
              rows={4}
              margin="normal"
            />
          </Grid>
        </Grid>

        {/* Инвентарный номер + QR + Наименование */}
        <Box mt={2} textAlign="center">
          <Typography variant="subtitle1" gutterBottom>
            {asset.inventory_number}
          </Typography>
          <div id="qrcode">
            <QRCodeSVG
              value={`http://localhost:3000/assets/${asset.id}`}
              size={200}
              bgColor="#ffffff"
              fgColor="#000000"
              level="H"
            />
          </div>
          <Typography variant="subtitle1" mt={0.5}>
            {asset.type} {asset.brand} {asset.model}
          </Typography>
        </Box>

        {/* Кнопка печати QR-кода */}
        <Box mt={0.5} display="flex" justifyContent="center">
          <Button variant="contained" color="primary" onClick={handlePrintQRCode}>
            Печать QR-кода
          </Button>
        </Box>

        {/* Кнопки управления */}
        <Box mt={4.5} display="flex" justifyContent="space-between" alignItems="center">
          <Button variant="outlined" onClick={() => navigate(-1)}>
            Назад
          </Button>
          <Box>
            <Button
              variant="contained"
              color="success"
              onClick={handleSave}
              sx={{ mr: 2 }}
            >
              Сохранить
            </Button>
            <Button
              variant="contained"
              color="error"
              onClick={handleDelete}
            >
              Удалить
            </Button>
          </Box>
        </Box>
      </Paper>
    </Container>
  );
};

export default AssetDetails;
