// frontend/src/components/DepartmentList.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Box
} from '@mui/material';

const DepartmentList = () => {
  const [departments, setDepartments] = useState([]);
  const [employeeCounts, setEmployeeCounts] = useState({});
  const [assetCounts, setAssetCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);

        // Для каждого отдела загружаем количество сотрудников и активов
        const empCounts = {};
        const assetCounts = {};

        for (let dept of deps) {
          const empRes = await axios.get(`http://localhost:5000/api/employees?department_id=${dept.id}`);
          empCounts[dept.id] = empRes.data.length;

          const assetRes = await axios.get(`http://localhost:5000/api/assets?department_id=${dept.id}`);
          assetCounts[dept.id] = assetRes.data.length;
        }

        setEmployeeCounts(empCounts);
        setAssetCounts(assetCounts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
        <Table size="medium"> {/* Увеличиваем размер таблицы */}
          <TableHead>
            <TableRow>
              <TableCell sx={{ fontSize: '16px' }}>Название</TableCell> {/* Увеличиваем размер шрифта */}
              <TableCell align="right" sx={{ fontSize: '16px' }}>Сотрудников</TableCell>
              <TableCell align="right" sx={{ fontSize: '16px' }}>Оборудования</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {departments.map(dept => (
              <TableRow
                key={dept.id}
                hover
                sx={{
                  '&:hover': {
                    backgroundColor: '#f5f5f5',
                  },
                  transition: 'background-color 0.2s',
                }}
              >
                <TableCell sx={{ fontSize: '16px' }}>{dept.name}</TableCell>
                <TableCell align="right" sx={{ fontSize: '16px' }}>{employeeCounts[dept.id] || 0}</TableCell>
                <TableCell align="right" sx={{ fontSize: '16px' }}>{assetCounts[dept.id] || 0}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
};

export default DepartmentList;
