// frontend/src/components/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemIcon, ListItemText, Typography, Divider, Link } from '@mui/material'; // Импортируем необходимые компоненты
import DashboardIcon from '@mui/icons-material/Dashboard';
import CategoryIcon from '@mui/icons-material/Category';
import MoveToInboxIcon from '@mui/icons-material/MoveToInbox';
import GroupIcon from '@mui/icons-material/Group';

const Sidebar = ({ role }) => {
  return (
    <div className="sidebar" style={{ width: '250px', padding: '20px' }}>
      <Typography variant="h6">IT-Инвентаризация</Typography>
      <Divider />
      <List>
        <ListItem button component={Link} to="/">
          <ListItemIcon><DashboardIcon /></ListItemIcon>
          <ListItemText primary="Оборудование" />
        </ListItem>

        <ListItem button component={Link} to="/categories">
          <ListItemIcon><CategoryIcon /></ListItemIcon>
          <ListItemText primary="Категории" />
        </ListItem>

        <ListItem button component={Link} to="/moves">
          <ListItemIcon><MoveToInboxIcon /></ListItemIcon>
          <ListItemText primary="Перемещения" />
        </ListItem>

        <ListItem button component={Link} to="/users">
          <ListItemIcon><GroupIcon /></ListItemIcon>
          <ListItemText primary="Пользователи" />
        </ListItem>
      </List>
    </div>
  );
};

export default Sidebar;
