// frontend/src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Sidebar from './components/Sidebar';
import AssetList from './components/AssetList';
import AddAsset from './components/AddAsset';
import AssetDetails from './components/AssetDetails';
import EmployeeList from './components/EmployeeList';
import Directory from './components/Directory';
import ChangesList from './components/ChangesList';
import Login from './components/Login';
import Header from './components/Header';

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
    window.location.href = '/assets';
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh' }}>
        {/* Шапка */}
        <Header username={currentUser?.username} role={currentUser?.role} onLogout={handleLogout} />

        <div style={{ display: 'flex', flexDirection: 'row', flexGrow: 1 }}>
          {/* Боковое меню */}
          <div style={{ width: 250, backgroundColor: '#f4f4f4', height: '100vh', position: 'fixed', top: 64, left: 0 }}>
            <Sidebar currentUser={currentUser} />
          </div>

          {/* Основной контент */}
          <div style={{ marginLeft: 250, flex: 1, padding: '20px', backgroundColor: '#fff' }}>
            <Routes>
              {/* Форма входа */}
              <Route path="/login" element={!currentUser ? <Login onLogin={handleLogin} /> : <Navigate to="/assets" />} />

              {/* Все остальные страницы */}
              <Route path="/assets" element={<AssetList currentUser={currentUser} />} />
              <Route path="/assets/:id" element={
                <AssetList currentUser={currentUser} />
              } />
              <Route path="/employees" element={
                <ProtectedRoute user={currentUser} requiredRole="admin">
                  <EmployeeList />
                </ProtectedRoute>
              } />
              <Route path="/directory" element={
                <ProtectedRoute user={currentUser} requiredRole="admin">
                  <Directory />
                </ProtectedRoute>
              } />
              <Route path="/changes" element={
                <ProtectedRoute user={currentUser} requiredRole="admin">
                  <ChangesList />
                </ProtectedRoute>
              } />
              <Route path="/" element={<Navigate to="/assets" replace />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
