import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [assetCounts, setAssetCounts] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загружаем сотрудников
        const empsRes = await axios.get('http://localhost:5000/api/employees');
        const emps = empsRes.data;
        setEmployees(emps);

        // Загружаем отделы
        const depsRes = await axios.get('http://localhost:5000/api/departments');
        const deps = depsRes.data;
        setDepartments(deps);

        // Подсчитываем оборудование для каждого сотрудника
        const assetCounts = {};

        for (let emp of emps) {
          const deptAssets = await axios.get(`http://localhost:5000/api/assets?department_id=${emp.department_id}`);
          assetCounts[emp.id] = deptAssets.data.length;
        }

        setAssetCounts(assetCounts);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
      }
    };

    fetchData();
  }, []);

  return (
    <Container maxWidth="md">
      <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>

        {/* Отображаем отделы и их сотрудников */}
        {departments.map(dept => {
          const deptEmployees = employees.filter(emp => emp.department_id === dept.id);

          // Проверяем, есть ли сотрудники в отделе
          if (deptEmployees.length === 0) {
            return null; // Не отображаем пустые отделы
          }

          return (
            <div key={dept.id}>
              <Typography
                variant="h6"
                gutterBottom
                style={{
                  fontWeight: 'bold',
                  textAlign: 'center',
                  fontSize: '20px',
                }}
              >
                {dept.name}
              </Typography>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>
                      <Typography style = {{fontSize: '20px'}}>
                        ФИО
                      </Typography>
                    </TableCell>
                    <TableCell align="right" style = {{fontSize: '20px'}}>Оборудования</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {deptEmployees.map(emp => {
                    return (
                      <TableRow key={emp.id}>
                        <TableCell>{emp.name}</TableCell>
                        <TableCell align="right">{assetCounts[emp.id] || 0}</TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </div>
          );
        })}
      </Paper>
    </Container>
  );
};

export default EmployeeList;
